--
-- Tablo için tablo yapısı `categories`
--

CREATE TABLE `categories` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `games_count` bigint(20) NOT NULL DEFAULT 0,
  `status` tinyint(4) NOT NULL DEFAULT 1 COMMENT '0:pasif 1:aktif',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Tablo döküm verisi `categories`
--

INSERT INTO `categories` (`id`, `name`, `slug`, `description`, `games_count`, `status`, `created_at`, `updated_at`) VALUES
(1, 'RPG', 'rpg', '<p style=\"margin: 0.5em 0px;\"><span>﻿</span><span style=\"font-family: Helvetica;\">Rol yapma oyunu&nbsp;(RYO),&nbsp;(</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">İngilizce</span><span style=\"font-family: Helvetica;\">: Role-Playing Game, RPG); oyuncuların kurgusal bir sahnede farklı karakterlerin rollerini üstlendikleri oyunların genel bir ailesidir. Oyuncular, belirli kurallar çerçevesinde oluşturulan karakterleri yöneterek bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">hikâyenin</span><span style=\"font-family: Helvetica;\">&nbsp;parçası olurlar.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Kalem ve kâğıt</span><span style=\"font-family: Helvetica;\">&nbsp;RYO denilen orijinal biçimi, baştan sona konuşma aracılığıyla iletilir; oysa canlandırmalı rol yapma oyunlarında (LARP - Live Action Role Playing), oyuncular, karakterlerinin eylemlerini fiziksel olarak oynarlar. Bu biçimlerin her ikisinde de her oyuncu tek bir karakterin rolünü yaparken; bazı oyuncular Oyun Yöneticisi görevini üstlenerek oyununun senaryosunu hazırlar, kullanılacak ilkeleri belirler ve oyuncu karakterlerin iradesi dışında gelişen olayları kontrol eder.</span><span style=\"line-height: 1; unicode-bidi: isolate; font-family: Helvetica;\">&nbsp;Belirsizlik faktörünün sağlanması için genellikle zarlar kullanılır. Kalem ve kâğıt RYO,&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Türkiye\'de</span><span style=\"line-height: 1; unicode-bidi: isolate; font-family: Helvetica;\">&nbsp;yaygın olarak&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; font-family: Helvetica;\">FRP</span><span style=\"line-height: 1; unicode-bidi: isolate; font-family: Helvetica;\">&nbsp;adıyla bilinir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica;\">RYO\'ların çok oyunculu metin tabanlı&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">MUD</span><span style=\"font-family: Helvetica;\">\'ları ve grafik arayüzlerini içeren birkaç çeşidi elektronik ortamda bulunur. Rol yapma oyunları, oyuncuların,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">oyun</span><span style=\"font-family: Helvetica;\">&nbsp;sürecinde belirli kullara göre kabiliyetleri gelişen bir karakteri veya karakter grubunu kontrol ettikleri rol yapma video oyunlarını da kapsar. Oyun mekanikleri sıkça kalem ve kâğıt RYO kurallarına dayanır; ama, işbirlikçi hikâye anlatımından daha fazla, karakter geliştirmenin üzerinde durur. Oyun dünyaları da kalem ve kâğıt&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">RYO</span><span style=\"font-family: Helvetica;\">&nbsp;dünyalarıyla benzerdir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica;\">Biçimlerin bu çeşitliliğine rağmen; rol yapma unsurları içeren&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">kart toplama oyunları</span><span style=\"font-family: Helvetica;\">&nbsp;ve savaş oyunları gibi bazı oyunlar bunlara dahil değildir. Rol yapma etkinliği, bazen, böyle oyunlarda bulunabilmektedir ama temel odak noktası değildir. Terim, bazen de rol&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">simülasyonu</span><span style=\"font-family: Helvetica;\">&nbsp;oyunları ve eğitim, antrenman ve akademik araştırmada kullanılmış egzersizleri açıklamak üzere kullanılır. Bu oyunlara örnek olarak, GTA Serisi,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">The Witcher 3: Wild Hunt</span><span style=\"font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">The Elder Scrolls V:Skyrim</span><span style=\"font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Baldur\'s Gate serisi</span><span style=\"font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Stardew Valley</span><span style=\"font-family: Helvetica;\">&nbsp;gösterilebilir.</span></p><blockquote><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Amacı\" style=\"font-size: 24px;\"><b>Amacı</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica;\">Rol yapma oyunları, sosyal etkileşimi ve iş birliğini vurguladıkları oyunların diğer birçok türünden esas olarak farklıdır. Hâlbuki, genel oyunlar, kart oyunları ve spor oyunları rekabeti vurgular.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica;\">Kalem ve kâğıt rol yapma oyunlarının hem yazarları hem de büyük yayıncıları, etkileşimli ve işbirlikçi hikâye anlatımının biçimi olması için onları vurgularlar; ancak, rol yapma oyunu dahilinde hiç gerçek hikâye yokken,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">romanlar</span><span style=\"font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">filmler</span><span style=\"font-family: Helvetica;\">&nbsp;gibi gerçek öyküler dikkate alınmaz. Olaylar, karakterler ve öykü yapısı yerine, öykü tecrübesinden bir fikir verir.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Hikâyeler</span><span style=\"font-family: Helvetica;\">&nbsp;gibi rol yapma oyunları, hayali gücünü tuttukları için başvururlar. Etkileşim, rol yapma oyunları ve geleneksel kurgu arasındaki kritik farktır. Hâlbuki, bir televizyon programının seyircisi pasif bir gözlemci, rol yapma oyunda bir oyuncu hikâyeyi etkileyen seçimler yapar. Bu gibi rol yapma oyunları, hikâye meydana getirmek amacıyla arkadaşların, küçük bir partide iş birliği yaptıkları hikâye anlatma oyunlarının eski bir geleneğini devam ettirmektedir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica;\">Basit rol yapma kalıpları, \"polisler ve hırsızlar\" ve \'\'</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">kovboylar</span><span style=\"font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">Kızılderililer</span><span style=\"font-family: Helvetica;\">\'\' gibi çocuk geleneksel çocuk oyunlarında bulunurken; rol yapma oyunları, oyun kolaylaştırıcıları ve etkileşimin ilkeleri gibi eklerle, bu temel kanıya, bir entelektüellik ve süreklilik seviyesi ilave eder. Rol yapma oyunundaki katılımcılar, özel karakterler ve gelişmekte olan bir plot oluşturacaklardır. İlkelerin ve oyunlardaki daha fazla veya daha az gerçeğe uygun bir kampanya sahnesinin tutarlı sistemi, güvensizliğin asılmasına yardım eder.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\".C3.87e.C5.9Fitleri\" style=\"\"></span><span class=\"mw-headline\" id=\"Çeşitleri\" style=\"font-size: 24px;\">Çeşitleri</span></b></h2><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\"Kalem_ve_k.C3.A2.C4.9F.C4.B1t\" style=\"\"></span><span class=\"mw-headline\" id=\"Kalem_ve_kâğıt\" style=\"font-size: 18px;\">Kalem ve kâğıt</span></b></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun, küçük bir sosyal toplantıda konuşma aracılığıyla yönlendirilir. GM, oyun dünyasını ve onun sakinlerini anlatır. Diğer oyuncular tasarlanmış karakterlerinin tasarlanmış eylemlerini anlatırlar ve GM sonları anlatır. Bazı sonlar oyun sistemi tarafından belirlenirler ve bazıları GM tarafından seçilirler.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bu, rol yapma oyunlarının ilk kez halka sevdirildikleri biçimidir. İlk kez ticarî olarak piyasada bulunan RYO&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">D&amp;D</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;düşlem yazını&nbsp;ve&nbsp;savaş oyunu&nbsp;hobisiyle esinlendi ve 1974\'te yayınlandı.&nbsp;D&amp;D\'nin tutulması, oyun konuları, ilkeleri ve tarzlarının geniş bir çeşitliliğiyle oyunları yayınlayan kalem ve kâğıt rol yapma oyunu endüstrisinin doğuşuna yol açtı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Günümüzde bazı firmaların (Wizards of the Coast, White Wolf, Steve Jackson Games) oyunları yaygın olarak oynanır. Ayrıca&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">bağımsız</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;olarak yayımlanan yaygın sistemler de vardır. Bazı oyuncular kendi yarattıkları dünyaları ve kural sistemlerini oynamayı tercih eder. Türkiye\'de en çok sevilerek oynanan RP\'ler; FRP örneklerinden, Ejderha Mızrağı Destanı, Unutulmuş Diyarlar, Yüzüklerin Efendisi ve Harry Potter Serisi, (Proudloud olarak da özel bir çeşite sahiptir Harry Potter) kitap serileridir. Bunun en büyük nedeni hayran sayısının yüksek olması ve kişilerin bu dünyaları yaşama arzusudur.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bu biçim, sıklıkla bir&nbsp;rol yapma oyunu&nbsp;olarak basit bir biçimde atfedilir. Bu&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">RPG</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;biçimini diğer kalıplardan ayırt etmek için retroyms&nbsp;rol yapma oyunu&nbsp;ya da&nbsp;masaüstü rol yapma oyunu, ne&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">kalem</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">kâğıt</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ne de bir masa gerekli olmamasına rağmen, bazen kullanılırlar. FRP, yani Fantastik Rol Yapma Oyunu, rol yapma oyununun bir alt türüdür.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\"Canland.C4.B1rmal.C4.B1\" style=\"\"></span><span class=\"mw-headline\" id=\"Canlandırmalı\" style=\"font-size: 18px;\">Canlandırmalı</span></b></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bir LARP doğaçlama tiyatro gibi daha iyi oynanır. Katımcılar onları anlatmak yerine karakterlerinin eylemlerini davranışlarıyla ortaya koyarlar ve asıl sahne, oyun dünyasının hayalî sahnesini temsil etmek için kullanılır. Oyuncular sıklıkla onların karakterleri olarak kostümlü olup uygun sahne donanımları kullanırlar ve buluşma yeri oyun dünyasının düşsel sahnesini çalmak için donatılabilmektedir. Diğer LARP\'lar,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">airsoft</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">tabancaları</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;veya köpük silahları gibi taklit cephaneyle fiziksel savaşı kullanırken; bazı canlı eylem rol yapma oyunları, sembolik olarak anlaşmazlıkları çözmek üzere özelliklerden&nbsp;</span><span style=\"font-family: Helvetica; font-size: 14px;\">taş-kâğıt-makası</span><span style=\"font-size: 14px; font-family: Helvetica;\"> veya kıyaslamayı kullanırlar.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">LARP\'lar boyca, birkaç bine yakın bir avuç dolusu oyuncudan ve sürece, birkaç güne yakın bir çift mesai saatinden değişime uğrarlar. Bir LARP\'taki oyuncu sayısı genellikle bir rol yapma oyunundakinden daha bol olduğu ve oyuncular ayrı fiziksel alanlarda etkileşime geçebildikleri için, bir öyküyü veya oyuncuları doğrudan doğruya eğlendirici sıkı bir şekilde koruma konusunda karakteristik olarak daha az önemi vardır ve oyun devreleri daha iyi paylaştırılmış bir tutumda sıklıkla yönetilirler.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Elektronik_ortam\" style=\"font-size: 18px; font-family: Helvetica;\"><b>Elektronik ortam</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Kalem ve kâğıt rol yapma oyunları,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">elektronik</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;biçimlerin bir çeşidine dönüştürülmüşler. 1974 öncesine kadar, D&amp;D sürümü olarak aynı yıl&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">lisanssız</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;sürümleri DND ve&nbsp;Dungeon&nbsp;gibi başlıklar altında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">üniversite</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;bilgisayar sistemlerinde geliştirildiler.&nbsp;Bu eski bilgisayar RPG\'leri, çoğalan rol yapma video oyunu tarzı yanında, bütün elektronik kumarı etkiledi. Bazı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">yazarlar</span><span style=\"font-family: Helvetica; font-size: 14px;\">, rol yapma oyunlarını, RYO stil mekaniğini kullanarak tek oyunculu oyunlar ve sosyal etkileşimi katarak&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">çok oyunculu oyunlar</span><span style=\"font-family: Helvetica; font-size: 14px;\">, birbirine geçmiş iki gruba ayırırlar.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">Lethal Love</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;bunun bir örneğidir.</span></p><h4 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Tek_oyunculu\" style=\"font-size: 18px;\"><b>Tek oyunculu</b></span></h4><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tek oyunculu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;rol yapma video oyunları, sahneler ve oyun mekaniği terminolojilerini fazlaca kurdukları&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Zindanlar ve Ejderhalar</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi rol yapma oyunlarında kökenlerle bilgisayar ve konsol oyunlarını dağınık olarak tanımlı bir tarzını oluştururlar. Bu çeviri, dünyanın görsel bir temsilini sağlayarak ama işbirlikçi, etkileşimli hikâye anlatımı üzerinde istatistiksel karakter geliştirme üzerinde durarak oyun tecrübesini değiştirir.</span></p><h4 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\".C3.87ok_oyunculu\" style=\"\"></span><span class=\"mw-headline\" id=\"Çok_oyunculu\" style=\"font-size: 18px;\">Çok oyunculu</span></b></h4><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Metin bazlı arayüz ve bir İnternet bağlantısı kullanarak oynanan&nbsp;çevrimiçi metin bazlı rol yapma oyunları&nbsp;çok oyunculudur. Bu oyunlardan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">MUD</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'lar,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">MUSH</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'ler ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">MU*</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'nun diğer çeşitleri gerçek zamanlı bir şekilde oynanır.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">Posta</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;oyunları tur bazlı bir şekilde oynanır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Çok oyunculu çevrimiçi rol yapma oyunları (MMORPG)</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;grafik arayüzleriyle MUD\'ların büyük ölçekli&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">sosyal</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;etkileşimini ve sürekli dünyasını birleştirirler. Çoğu MMORPG karakterine uygun rol yapmayı etkin bir şekilde desteklememektedir; ancak oyuncular diğer oyuncularla iş birliği yaptıkları sürece oyunların iletişim işlevlerini rol yapmak için kullanabilirler.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Klasik rol yapma oyunlarında bulunan ortam ve oyun mekanikleri içeren bilgisayar oyunları ise \"bilgisayar rol yapma oyunları\" olarak adlandırılırlar. Bilgisayar endüstrisinin sık kullanımı ile RYO şimdilerde daha çok bu oyunları anlatmakta ve klasik oyunlara ise, kâğıt kalem rol yapma oyunu ve masaüstü rol yapma oyunu adı verilir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">İnternet üzerinde de yöneticilerin (DM veya GM), sanal forumlar oluşturarak, çok büyük kitleler için yarattıkları RYO\'lar da (kısaca RP olarak da bilinir) yeni bir çeşittir. RP\'ler de en büyük fark bir DM (oyun yöneticisi) olmadan da kişilerin, birbirleriyle kurgu yaratıp, oynayabilmesidir. Masaüstü RYO\' en fazla 10-12 kişilik bir gruba hitap ederken, sanal forumlar da bu sayı 100 kişiyi geçebilir. Ayrıca programların getirdiği pratik çözümlerle de, oyunlar etkileşimli bir hale gelir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Roller\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Roller</b></span></h2><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\"Oyun_Y.C3.B6neticisi\" style=\"\"></span><span class=\"mw-headline\" id=\"Oyun_Yöneticisi\" style=\"font-size: 18px;\">Oyun Yöneticisi</span></b></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Belli kurallar çerçevesinde, oyun yöneticisinin (</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Dungeon Master</span><span style=\"font-size: 14px; font-family: Helvetica;\">) yazdığı bir hikâyeyi oyunculara oynatmasıdır. Oyun sırasında oyun yöneticisi oyuncuların gözü ve kulağı olur. Yönetici oyuncuların hangi durumda olduğunu söyler; oyuncular da ona göre yöneticiye ne yaptıklarını söylerler. Oyun yöneticisinin rolü&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">video oyunlarındaki</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;bilgisayarın rolüne benzetilebilir. Oyun yöneticisi oyunu yönetirken kararlarını farklı esneklik seviyelerinde veriyor olabilir. Oyunu tamamen keyfi olarak yönlendirebileceği gibi oyunu yönetirken bir kural kitabında yazan kaideleri ve şans faktörünü belirlemek için zarlar kullanabilir. Tipik bir oyun süreci, oyun yöneticisinin dünya durumu tasviri ve oyuncuların buna verdiği tepkiler şeklinde döngüsel olarak ilerler.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Oyuncu_karakter\" style=\"font-size: 18px; font-family: Helvetica;\"><b>Oyuncu karakter</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Bir oyuncu karakter bir oyuncu tarafından kontrol edilen kurgusal dünyadaki bir karakterdir. Her oyuncu tipik olarak bir karakteri kontrol eder. Oyuncu karakterler oyunun&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">baş kahramanları</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;olur. Ayrıca PC (</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">İngilizce</span><span style=\"font-family: Helvetica; font-size: 14px;\">: Player Character) adıyla da bilinir.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Oyuncu_olmayan_karakter\" style=\"font-family: Helvetica; font-size: 18px;\"><b>Oyuncu olmayan karakter</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Oyuncu karakterlerin aksine,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">oyuncu olmayan karakterler</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;oyun yöneticisi (Kağıt ve Kalem), oyun motoru (Video oyunu) ya da oyun yöneticisinin yardımcıları (LARP) tarafından kontrol edilir. Oyuncu olmayan karakterler kurgusal dünyanın nüfusunu oluşturur. Bu karakterler&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">karşı kahraman</span><span style=\"font-family: Helvetica; font-size: 14px;\">, dost ya da izleyici rollerini taşır.&nbsp;Yaygın olarak&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">NPC</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;(</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">İngilizce</span><span style=\"font-family: Helvetica; font-size: 14px;\">: Non-Player Character) adıyla bilinir.</span></p></blockquote>', 4, 1, '2021-12-28 06:57:07', '2022-02-23 12:47:50'),
(2, 'FPS', 'fps', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Birinci şahıs nişancı&nbsp;(</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İngilizce</span><span style=\"font-size: 14px; font-family: Helvetica;\">:&nbsp;First-person shooter, kısaca&nbsp;FPS) oyuncu karakterinin kendi gözünden oynandığı oyun türüdür ve nişancı video oyunlarının yan türüdür. Tüm nişan türündeki oyunlar gibi, \"bir serüven, bir veya birden fazla uzun menzilli silah ve değişen sayılarda düşmanlar\" tabiriyle anılır. Birinci Şahıs Nişancı türündekiler Birinci Şahıs Bakış Açısı görüntüsüyle değer kazanır ve oyuncu karakterinin oyunun dünyasına görsel bir bakış açısı sunar. Karakter itibarıyla emirleri harfi harfine yerine getirir ve beklenen hareketleri yapar. Oyunlar esas olarak araçları kullanmayı gerektirmesi daha çok araç simulasyon oyunlarına benzemesine yol açmaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Video oyunlarındaki modern çağ, FPS 3 Boyutlu grafikler gibi yeni teknolojileri, çevrimiçi oyun ve kullanmak için bir yol gösterici olmuştur. Birinci Şahıs Nişancı türündeki oyunlardaki şiddet unsurları diğer türleri de etkilemiş ve oyunlarda genel anlamda şiddet ve gerçeklik payı artmıştır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Birinci şahıs nişancı,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">nişancı oyunlarının</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;bir alt maddesidir. Diğer birçok nişancı oyunları alt maddeleri gibi, birinci şahıs bakış açısında oynanılır, birinci şahıs oyunlarda silah kullanmak, uçak sürmek, oyunda dikkat toplamak gibi unsurlar diğer bakış açılarına göre daha kolay gerçekleşir. 1990\'lardan itibaren Birinci Şahıs bakış açısı türündeki oyunlar geliştirilmeyi başlanmıştır ve şu andaki birçok seri birinci şahıs kamerasını tercih etmiştir.</span><span style=\"font-family: Helvetica; font-size: 14px;\"><br></span></p>', 3, 1, '2022-01-03 14:07:41', '2022-01-13 12:45:25'),
(3, 'Strateji - Simülasyon', 'strateji-simulasyon', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Strateji video oyunu, ustaca düşünmeye ve zafere ulaşmak için planlamaya odaklanan bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">türüdür</span><span style=\"font-size: 14px; font-family: Helvetica;\">.&nbsp;Strateji video oyunları, stratejik, taktiksel ve bazen lojistik zorlukları ve keşifleri vurgular. Genel olarak, oyunun dönüşüme dayalı mı yoksa gerçek zamanlı mı olduğuna ve oyunun strateji veya taktik üzerinde yoğunlaşıp durmadığına bağlı olarak dört alt tipe ayrılırlar.</span></p><p><span style=\"font-size: 14px; font-family: Helvetica;\">Simülasyon oyunu, genellikle gerçek dünyadaki etkinlikleri yakından&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">simüle</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;etmek için tasarlanmış&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunlarıdır</span><span style=\"font-size: 14px; font-family: Helvetica;\">.&nbsp;Simülasyon oyunları&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">eğitim</span><span style=\"font-size: 14px; font-family: Helvetica;\">, analiz veya tahmin gibi çeşitli amaçlar için bir oyun biçiminde çeşitli etkinlikleri gerçek hayattan kopyalamaya çalışır. Genellikle oyunda kesin olarak tanımlanmış hedefler yoktur, oyuncunun bir karakteri veya çevreyi serbestçe kontrol etmesine izin verilir.&nbsp;İyi bilinen örnekler&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">savaş oyunları</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">iş oyunları</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">rol yapma simülasyonudur</span><span style=\"font-size: 14px; font-family: Helvetica;\">.</span><br></p>', 3, 1, '2022-01-04 14:50:39', '2022-02-16 10:52:09'),
(4, 'Spor - Yarış', 'spor-yaris', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Spor oyunları, geleneksel sporların oynanışları taklit edilerek geliştirilmiş video oyunlarıdır. Bu tür, epey popüler olmakla birlikte, çok satan oyunlar listesine pek çok ürün vermektedir. Neredeyse her aşina olduğumuz spor, bir video oyunu olarak raflarda yerini almıştır. Başlıcaları şunlardır:&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Futbol</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">basketbol</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">beyzbol</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Amerikan futbolu</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">boks</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">güreş</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">kriket</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">golf</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">buz hokeyi</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">tenis</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">bovling</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ragbi</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">avcılık</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">balıkçılık</span><span style=\"font-size: 14px; font-family: Helvetica;\">.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bazı oyunlar (örneğin;&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Fifa</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisi gibi), sporu birebir oynamayı esas alırken, diğerleriyse (örneğin;&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Championship Manager</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi) sporun arkasındaki stratejiyi öne çıkarır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Birkaç oyun serisi, gerçek ligleri baz alarak; kulüplerin isim haklarını lisanslar ve oyuncuların karakteristiklerine yer verir. Ayrıca istatiki değişiklikleri, yıldan yıla yansıtmak üzere güncelleştirir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Yarış oyunu, türü oyuncunun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">birinci şahıs</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ya da&nbsp;üçüncü şahıs&nbsp;perspektifinde, kara, hava ya da deniz&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">taşıtlarının</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yarıştığı bir mücadeleye katıldığı&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu türüdür</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Yarışmalar gerçek dünya yarış liglerini baz alıyor olabileceği gibi tamamen hayali bir kuruluma da sahip olabilir. Bu tür oyunların gerçekçiliği yüksek (</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Simülasyon</span><span style=\"font-size: 14px; font-family: Helvetica;\">) ya da düşük (</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Arcade</span><span style=\"font-size: 14px; font-family: Helvetica;\">) olabilir. Yarış oyunları aynı zamanda&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">spor oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;olarak sınıflandırılabilir.</span><span style=\"color: rgb(32, 33, 34); font-family: Helvetica; font-size: 14px;\"><br></span></p>', 1, 1, '2022-01-12 16:30:40', '2022-02-14 13:43:20');

-- --------------------------------------------------------

--
-- Tablo için tablo yapısı `developers`
--

CREATE TABLE `developers` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `games_count` bigint(11) DEFAULT NULL,
  `status` tinyint(4) NOT NULL DEFAULT 1 COMMENT '0:pasif 1:aktif',
  `image` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Tablo döküm verisi `developers`
--

INSERT INTO `developers` (`id`, `name`, `slug`, `description`, `games_count`, `status`, `image`, `deleted_at`, `created_at`, `updated_at`) VALUES
(1, 'Rockstar North', 'rockstar-north', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Rockstar North Ltd.&nbsp;(eski adı&nbsp;DMA Design Ltd.),&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İskoçya</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'nın&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Edinburg</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;şehri merkezli&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;geliştirme şirketidir.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Take-Two Interactive</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;şirketine ait&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Rockstar Games</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'in bir koludur.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto III</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto: Vice</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto: San Andreas</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto V</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'ın birincil geliştiricileridir. Ayrıca çok satan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto IV</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'ün yapımında büyük rol oynayan şirkettir.</span><br></p>', 2, 1, '/uploads/developers/rockstar-north/rockstar-north.png', NULL, '2021-12-28 06:32:08', '2022-01-13 12:02:17'),
(3, 'DICE', 'dice', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">EA Digital Illusions CE AB&nbsp;(ticari ismi:&nbsp;DICE),&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İsveç</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli 1992 yılında kurulmuş bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu geliştiricisidir</span><span style=\"font-size: 14px; font-family: Helvetica;\">. 2006 yılı itibarıyla tamamen&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Electronic Arts</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;mülkiyetindedir. En çok&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Battlefield</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;video oyunu serisi,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Mirror\'s Edge</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Star Wars: Battlefront</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile tanınır. Şirket, alt kuruluşu olan Frostbite Labs aracılığıyla&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Frostbite</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyun motorunu da geliştirmekte.</span></p>', 1, 1, '/uploads/developers/dice/dice.jpg', NULL, '2022-01-03 14:10:35', '2022-01-04 12:47:51'),
(4, '4A Games', '4a-games', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">4A Games Limited,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sliema</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Malta</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir Ukraynalı-Maltalı video oyunu geliştiricisidir. Şirket, 2006 yılında&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Ukrayna</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kiev</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şehrinde kuruldu ve 2014 yılında merkez ofisini&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Malta</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ya taşıdı,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kiev</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ofisi ise alt stüdyo olarak çalışmaya devam ediyor. 4A Games, en çok&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro serisi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile tanınmaktadır.</span></p><p><span style=\"text-align: var(--bs-body-text-align); font-size: 18px; font-family: Helvetica;\"><b>Geçmişi</b></span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bir diğer Ukraynalı oyun geliştirici&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">GSC Game World</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketinin eski çalışanları tarafından&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">S.T.A.L.K.E.R.: Shadow of Chernobyl</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yayınlanmadan 1 yıl önce kuruldu. Bilhassa&nbsp;S.T.A.L.K.E.R.&nbsp;serisinin yapımında kullanılan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">X-Ray Engine</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyun motorunun yapımında çalışmış programcılar Oles Shyskovtsov ve Oleksandr Maksymchuk başı çekmiştir.&nbsp;S.T.A.L.K.E.R serisinin yapımında kullanılan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">X-Ray Engine</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyun motorunda çalıştıktan sonra, edindikleri birikim ve deneyimi kendi oyun motorları olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">4A Engine</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'i yapmakta kullandılar ve böylece 4A Games\'in baş teknik sorumluları oldular.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">4A Engine</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'in geliştirilmesi firmanın popüler oyunu&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro 2033</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ün geliştirilmesine de öncülük etmiştir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"text-align: var(--bs-body-text-align); font-size: 18px; font-family: Helvetica;\"><b>Metro Serisi</b></span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Firmanın ilk oyun projesi 2006\'da geliştirilmeye başlandı, bunun ortaya çıkmasıyla birlikte geliştiriciler popüler&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Rus</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yazar&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Dmitry Glukhovsky</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile birlikte çalıştıklarını açıkladılar. 2009 yılındaysa&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Leipzig</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'deki&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Oyun Kongresi</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nde ilk oyunları&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro 2033</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ü resmen duyurdular.&nbsp;Oyun, Mart 2010\'da dağıtımcı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">THQ</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından yayınlandı.&nbsp;Göz alıcı görselleri ve oyun motoruyla oldukça iyi eleştiriler aldı.&nbsp;Bu durum, 2011 E3 fuarında duyurulan şirketin ikinci oyunu&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro: Last Light</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın geliştirilmesine de öncülük etti. 4A Games bu devam oyununu da&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">4A Engine</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile geliştirmiştir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ne yazık ki,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro: Last Light</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yapım sürecinde bir takım sorunlarla karşılaştı. Çıkış tarihi bir hayli ertelenen oyun, asıl çıkış tarihi olan 2012 yılından 2013\'ün ilk çeyreğine kadar sarktı.&nbsp;Şirket için en önemli olay Ocak 2013\'te meydana gelen 4A Games\'in orijinal dağıtımcısı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">THQ</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun iflastan ötürü kapatılması ve bünyesindeki oyun geliştirme şirketlerinin açık artırmaya çıkarılmasıydı. Metro serisinin yayım hakları 22 Ocak\'ta 5.8 milyon dolara&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Deep Silver</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'a satıldı, neyse ki proje iptal edilmedi ve geliştirme takımı oyunlarını yapmaya devam edebildi.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">30 Mart 2014 tarihinde, Metro Redux adı altında ilk iki Metro oyununun yeniden yayınlanacağı duyumları yayıldı ve ertesi gün bu duyumlar onaylandı. Redux derlemesi, sekizinci nesil platformları için Ağustos 2014\'te yayınlandı.&nbsp;2017 yılında, şirket sanal gerçeklik oyun olan Arktika\'yı piyasaya sürdü.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">2017 E3 toplantısı sırasında, 11 Haziran 2017 tarihli Microsoft basın toplantısında, 2018 yılı için Metro Exodus duyuruldu. Hem oyunun duyurusu yapıldı, hem de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft</span><span style=\"font-size: 14px; font-family: Helvetica;\">’un&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;donanımını 4K odaklı güncellediği Xbox One X\'in grafiksel gösterimi için bir nevi vitrin görevi gördü.&nbsp;Oyun 2019 yılında piyasaya sürüldü.</span></p>', 1, 1, '/uploads/developers/4a-games/4a-games.png', NULL, '2022-01-03 16:24:15', '2022-01-04 12:35:25'),
(6, 'Relic Entertainment', 'relic-entertainment', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">﻿</span><span style=\"font-size: 14px; font-family: Helvetica;\">Relic Entertainment, PC platformunda&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">3D</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">gerçek zamanlı strateji</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunları üreten&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kanada</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli oyun firmasıdır.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Geçmiş\" style=\"font-size: 18px; font-family: Helvetica;\"><b>Geçmiş</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Relic, 1997 yılında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Alex Garden</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Luke Moloney</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kanada</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nın&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Britanya Kolumbiyası</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;eyaletine bağlı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Vancouver</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şehrinde kurulmuştur. Firmanın çıkardığı ilk oyun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Homeworld</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yüksek satış rakamlarına ulaşınca firma büyük bir prestij kazanmıştır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Daha sonra firmanın ikinci oyunu olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Impossible Creatures</span><span style=\"font-size: 14px; font-family: Helvetica;\">, 2002 yılında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından piyasaya sürüldü.&nbsp;Homeworld&nbsp;kadar dikkate alınmasa da başarılı sayıldı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Eylül 2003,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Homeworld 2</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;raflardaki yerini aldı. Grafiklerdeki yenilikler ve oynanış ile ilgili değişiklikler ilgi çekici görünse de genel olarak serinin ilk oyunu kadar etkileyici bulunmadı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">27 Nisan 2004, yayıncı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">THQ</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile 10 milyon&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">$</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;karşılığı anlaşma imzalayan Relic, 20 Eylül 2004\'te başarılı sayılabilecek&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Warhammer 40,000: Dawn of War</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyununu piyasaya çıkardı.&nbsp;Dawn of War&nbsp;başarısının ardından Relic, 19 Eylül 2005\'te&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Winter Assault</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;adında bir ek paket çıkardı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Relic 13 Mart 2006 tarihinde ilk ve tek konsol oyunu&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">The Outfit</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'i çıkardı. Oyun tek-kişilik oyun modu yüzünden büyük eleştirilere maruz kaldı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">11 Eylül 2006 ise&nbsp;Relic&nbsp;için bir dönüm noktası oldu.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">II. Dünya Savaşı</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nı konu edinen,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Essence Engine</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Havok</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyun motorlarını kullanan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Company of Heroes</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyun piyasasını sarstı.&nbsp;Company of Heroes&nbsp;devrim niteliğindeki yeni nesil grafik efektleri, dinamik ışıklandırma teknikleri ve kolay oynanış özelliği ile&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">RTS</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;türüne birçok yenilik getirdi. Oyun birçok eleştirmen tarafından olumlu not aldı ve birçok ödül kazandı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Kısa bir süre sonra 9 Ekim 2006\'da&nbsp;Dawn of War&nbsp;\'ın ikinci ek paketi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Warhammer 40,000: Dawn of War: Dark Crusade</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'i piyasaya süren&nbsp;Relic, 24 Eylül 2007\'de ise&nbsp;Company of Heroes\'un tek başına çalışabilen ek paketi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Company of Heroes: Opposing Fronts</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile bir kez daha RTS severleri kendine hayran bıraktı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Son olarak dünyaca ünlü Age of Empires serisinin dördüncü oyununu geliştirdiler.</span></p>', 1, 1, '/uploads/developers/relic-entertainment/relic-entertainment.png', NULL, '2022-01-10 11:52:41', '2022-01-10 17:37:07'),
(9, 'Bend Studio', 'bend-studio', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Bend Studio, Bend,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Oregon</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">merkezli bir Amerikan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;geliştiricisidir. 1992\'de kurulan stüdyo, en iyi Bubsy 3D, Siphon Filter serisi ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Days Gone</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'u geliştirmesiyle tanınır. 2000 yılından bu yana Bend Studio,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sony Interactive Entertainment</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için birinci taraf geliştiricidir.</span><br></p>', 1, 1, '/uploads/developers/bend-studio/bend-studio.jpg', NULL, '2022-01-12 16:47:18', '2022-01-12 17:15:28'),
(10, 'Playground Games', 'playground-games', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Playground Games Limited,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İngiltere</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İngiliz</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu geliştiricisi</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Daha büyük&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Forza</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisinin bir parçası olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Forza Horizon</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisini geliştirmesiyle tanınır. 2018\'de Playground Games,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Studios\'un</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;(artık Xbox Game Studios olarak biliniyor) bir parçası oldu. Ayrıca şu anda&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Fable</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisindeki bir sonraki oyunun geliştirme aşamasındalar.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Tarih\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Tarih</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Playground Games,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Trevor Williams</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Nick Wheelwright</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından kuruldu; stüdyo Kasım 2009\'da duyuruldu ve açılışı 2010\'un başlarında yapılması planlanıyor.&nbsp;Playground Games\'ten önce Williams, Swordfish Studios\'u kurmuştu ve bu stüdyo, defalarca el değiştirdikten sonra, Wheelwright şirketinin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">CEO\'su</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;olduğu&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Codemasters</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile birleşti. Mayıs 2010\'da, Playground Games oyun geliştirmeye bir yıl başlangıçta avantaj sağlamak için Codemasters çalışanları hakkında suçlamalarda bulundu.&nbsp;Playground Games, 2012 yılında ilk projesi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Forza Horizon\'u</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;duyurdu. Başlık, Turn 10 Studios ile işbirliği içinde geliştirildi ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Studios</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından o yılın Ekim ayında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox 360</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için yayınlandı. Stüdyo, Eylül 2014\'te Xbox 360 ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One için</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Forza Horizon 2\'yi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve Eylül 2016\'da Xbox One ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows için</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Forza Horizon 3\'ü</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;piyasaya sürdü. &nbsp;</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Şubat 2014\'te Playground Games,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">mobil oyunlara</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;odaklanan yeni bir geliştirme ekibi kurduklarını duyurdu.&nbsp;Şubat 2017\'de stüdyo, yarış dışı bir açık dünya video oyunu projesine odaklanarak ikinci bir stüdyo açma planlarını ayrıntılı olarak açıkladı. Kasım ayında, yeni stüdyoya 17.000 metrekareden fazla ofis alanı sağlayacak olan Leamington Spa\'daki St. Albans House\'u kiraladıklarını duyurdular.&nbsp;Her iki stüdyo için beklenen kişi sayısı 200\'den fazlaydı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">E3 2018\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft</span><span style=\"font-size: 14px; font-family: Helvetica;\">, Microsoft Studios\'un (artık Xbox Game Studios olarak biliniyor) bir parçası olan Playground Games\'i satın aldıklarını duyurdu.&nbsp;Aynı zamanda Microsoft Studios, 2 Ekim 2018\'de piyasaya sürülecek olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Forza Horizon 4\'ü</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve stüdyonun duyurulmamış bir AAA Açık Dünya Aksiyon RPG projesi üzerinde çalıştığını ve daha sonra&nbsp;Fable&nbsp;serisinde yer aldığını&nbsp;duyurdu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Son olarak Forza Horizon 5 oyununu geliştiren ekip bu alanda oldukça başarılı işlere imza atıyor.</span></p><p><br></p>', 1, 1, '/uploads/developers/playground-games/playground-games.png', NULL, '2022-01-13 07:52:42', '2022-01-13 12:02:17'),
(11, 'Krafton Game Union', 'krafton-game-union', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Krafton Game Union&nbsp;,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Güney Kore</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketidir. Şirket, Bluehole adıyla Mart 2007 tarihinde Chang Byung-gyu tarafından kurulmuştur.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">TERA</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayerUnknown\'s Battlegrounds</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PUBG: NEW STATE</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunları ile bilinmektedir. 5 Kasım 2018\'de birleşik bir marka altında Krafton Game Union adıyla yeniden kurulmuştur.</span><br></p>', 1, 1, '/uploads/developers/krafton-game-union/krafton-game-union.png', NULL, '2022-01-13 12:12:27', '2022-01-13 12:53:53'),
(12, 'Ubisoft Blue Byte', 'ubisoft-blue-byte', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Ubisoft Blue Byte, 1988 yılında kurulmuş bir oyun firmasıdır. Merkezi&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Almanya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nın&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Düsseldorf</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kentindedir. 2001 yılında bir başka oyun firması olan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Ubisoft</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından alınmış olup hala bu firma altında faaliyetlerine devam etmektedir. Geliştirdiği Anno serisi ile bilinir.</span><br></p>', 1, 1, '/uploads/developers/ubisoft-blue-byte/ubisoft-blue-byte.png', NULL, '2022-01-13 14:22:22', '2022-01-14 09:27:10'),
(13, 'Amplitude Studios', 'amplitude-studios', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Amplitude Studios SASU, Paris merkezli bir Fransız video oyunu geliştiricisi.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ocak 2011\'de eski Ubisoft çalışanları Romain de Waubert de Genlis ve Mathieu Girard tarafından kurulan stüdyo, Haziran 2016\'da Sega tarafından satın alındı.</span></p>', 1, 1, '/uploads/developers/amplitude-studios/amplitude-studios.png', NULL, '2022-02-16 09:27:43', '2022-02-16 11:25:48');
INSERT INTO `developers` (`id`, `name`, `slug`, `description`, `games_count`, `status`, `image`, `deleted_at`, `created_at`, `updated_at`) VALUES
(14, 'Kojima Productions', 'kojima-productions', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Kojima Productions Co., Ltd.,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Japonya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu geliştiricisidir</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Şirket, 1 Nisan 2005 tarihinde Hideo Kojima tarafından&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Konami</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yan kuruluşu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;olarak kurulmuş olup&nbsp;2015 yılında bağımsız bir stüdyo haline geldi.&nbsp;Şirketin merkezi&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tokyo</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Shinagawa</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;semtinde yer almaktadır.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Tarih\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Tarih</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Şirket, 1 Nisan 2005 tarihinde, Konami,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hideo Kojima</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nın Konami Computer Entertainment Japan\'deki (KCEJ) ekibi de dahil olmak üzere birçok yan kuruluşu birleştirdikten sonra kuruldu.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kojima</span><span style=\"font-size: 14px; font-family: Helvetica;\">, birleşmenin onu KCEJ başkan yardımcısı olarak sahip olduğu işletme yönetimi ve idari yüklerden kurtardığını ve Kojima Productions\'ın başkanı olarak oyun yaratmaya odaklanabileceğini belirtti. Belirtti:</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Biraz daha ayrıntıya girmek için, şimdi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Konami</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'de yönetim kurulu üyelerinden biri olarak konumdayım ve şirket içinde bir sorumluluğum ve saygım olduğu doğru. Ancak, bir yaratıcı olarak \"Bu yeni oyunu oluşturmak istiyorum\" dediğimde, hala yirmi yıl önce olduğu gibi! İnsanlar hala \"bu satacak mı, bu iyi olacak mı?\" Diye soruyor. Hiç kimse bunun bir ani vuruş olup olmadığını gerçekten anlamıyor. Sadece üst düzey yöneticilerden değil, geliştirme ekibinden bile bahsediyorum! Ama bu benim meydan okumam. İlginç olan bu. Anında göremediklerini anlamıyorlar, bu yüzden hemen \"bu harika bir fikir\" diyemiyorlar. Bu benim meydan okumam ve onlara sunduğum zaman bu yeni şeyi yaratmak istediğimi söyleyerek memnuniyetim. Ve eğer bu konuda daha fazla \'boost\' alırsam - \"Hayır! Ne hakkında konuştuğunu bilmiyoruz!\" İşte bu aslında eğlenceli kısım.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Şirketin ilk sürümü, 2011 yılında Metal Gear oyunlarının bir derlemesi olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metal Gear Solid HD</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;Koleksiyonu idi.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metal Gear Solid 3: Snake Eater</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için bir liman olarak&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Nintendo 3DS</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metal Gear Solid: Snake Eater 3D</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'yi yayınladılar. Şirket,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Konami</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metal Gear Solid V: Ground Zeroes</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metal Gear Solid V: The Phantom Pain</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;dahil olmak üzere iki son video oyunu üretti.</span></p>', 1, 1, '/uploads/developers/kojima-productions/kojima-productions.jpg', NULL, '2022-02-16 12:56:00', '2022-02-23 12:45:10'),
(15, 'FromSoftware', 'fromsoftware', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">FromSoftware, Inc.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Japonya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu geliştiricisidir</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Şirket, 1 Kasım 1986 tarihinde kurulmuş olup özellikle&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Armored Core</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Souls</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisi ile&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bloodborne</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunları ile bilinmektedir. Şirketin merkezi&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tokyo</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Shibuya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;semtinde yer almaktadır. Son olarak geliştirdiği Elden Ring adlı oyun ile oyun severlerin büyük beğenisini toplamayı başarmıştır.</span><br></p>', 1, 1, '/uploads/developers/fromsoftware/fromsoftware.jpg', NULL, '2022-03-03 12:22:49', '2022-03-03 13:00:19'),
(16, 'Maxis', 'maxis', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Maxis Software,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ABD</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir oyun geliştirici şirket.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">The Sims</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">SimCity</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisinin ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Spore</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yapımcısıdır. EA, 5 Mart 2015\'te resmi olarak stüdyoyu kapamıştır. Geliştirdiği oyunlar arasında en ses getiren sims serisi olmuştur.</span><br></p>', 0, 1, '/uploads/developers/maxis/maxis.png', NULL, '2022-03-03 13:02:08', '2022-03-03 13:02:08');

-- --------------------------------------------------------

--
-- Tablo için tablo yapısı `games`
--

CREATE TABLE `games` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `category_id` bigint(20) UNSIGNED DEFAULT NULL,
  `developer_id` bigint(20) UNSIGNED DEFAULT NULL,
  `publisher_id` bigint(20) UNSIGNED DEFAULT NULL,
  `sys_req_min_id` bigint(20) UNSIGNED DEFAULT NULL,
  `sys_req_rec_id` bigint(20) UNSIGNED DEFAULT NULL,
  `game_details_id` bigint(20) UNSIGNED DEFAULT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `sub_title` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `status` tinyint(4) NOT NULL DEFAULT 1 COMMENT '0:pasif 1:aktif',
  `hit` bigint(20) NOT NULL DEFAULT 0,
  `cover_image` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `image1` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `image2` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image3` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image4` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image5` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image6` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image7` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `video1` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `video2` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `video3` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `video4` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `video5` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
