INSERT INTO `games` (`id`, `name`, `category_id`, `developer_id`, `publisher_id`, `sys_req_min_id`, `sys_req_rec_id`, `game_details_id`, `slug`, `sub_title`, `description`, `status`, `hit`, `cover_image`, `image1`, `image2`, `image3`, `image4`, `image5`, `image6`, `image7`, `video1`, `video2`, `video3`, `video4`, `video5`, `deleted_at`, `created_at`, `updated_at`) VALUES
(1, 'Grand Theft Auto: Vice City', 1, 1, 1, 1, 1, 1, 'grand-theft-auto-vice-city', 'Grand Theft Auto: Vice City, Rockstar North tarafından geliştirilen ve Rockstar Games tarafından yayımlanan, 2002 aksiyon-macera temalı video oyunu.', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">2001 yılında çıkan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto III</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'ten bu yana,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto&nbsp;serisinin</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;giriş oyunlarından biri oldu. 1986 yıllarında geçen oyun hikâyesi,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Miami</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'yi baz alarak tasarlanmış&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Vice City</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'de,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tony Montana</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'dan esinlenilerek tasarlanmış,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İtalyan</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;bir mafya üyesi olan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tommy Vercetti</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'yi ve yaşadığı macerayı anlatmaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Oyun&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 2</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 3</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox 360</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PC</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">iOS</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Android</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;sistemlerini desteklemektedir. PlayStation 2 konsolları için&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzey Amerika</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'da 29 Ekim 2002,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avrupa</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avustralya</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'da ise 8 Kasım 2002\'de satışa sunuldu. Oyunun&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Windows</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;sürümü&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzey Amerika</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'da 1 Mayıs 2003,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avrupa</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'da ise 5 Mayıs 2003\'te satışa sunuldu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Oyun,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">üçüncü şahıs bakış açısıyla</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;oynanmaktadır. Açık dünya tasarımı, oyuncunun iki ana adadan oluşan Vice City\'de özgürce dolaşmasını sağlar. Oyunun konusu ise gerçek dünyadada,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Miami</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'deki&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kübalılar</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Haitililer</span><span style=\"font-family: Helvetica; font-size: 14px;\">, motorcu çeteleri, 1980\'lerdeki&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">crack salgını</span><span style=\"font-family: Helvetica; font-size: 14px;\">, Miami\'nin uyuşturucu pazarlayıcısı aileleri ve glam metalin egemenliği gibi Miami\'deki birçok gerçek dünya insanı ve olayına dayanmaktadır. Oyun ayrıca&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Scarface</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Miami Vice</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;dahil olmak üzere dönemin film dünyasından da etkilendi. Geliştirme çalışmalarının birçoğu ilham, ve zaman dilimine uyacak şekilde oyun dünyasını oluşturdu; geliştirme ekibi dünyayı yaratırken Miami\'de kapsamlı saha araştırması yaptı.&nbsp;Vice City, yayımlandıktan sonra özellikle müziğine, oynanışına ve açık dünya tasarımına yönelik övgülerle beğeni topladı. Aynı zamanda şiddet ve ırksal grupların tasviri konusunda da tartışmalara yol açarak davaları ve protestoları ateşledi. Sonuç olarak&nbsp;Vice City&nbsp;2002\'de, 17,5 milyondan fazla satarak en çok satan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;oldu.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Hikâye\" style=\"font-family: Helvetica; font-size: 24px;\"><b>Hikâye</b></span></h2><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Giriş\" style=\"font-family: Helvetica; font-size: 20px;\"><b>Giriş</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Sonny Forelli isimli karakter, Liberty City\'de bulunan en büyük üç&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">mafya ailesinden</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;birinin reisidir. Oyuncunun kontrol ettiği&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">Tommy Vercetti</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;karakteri ise Sonny ile beraber büyümüş ve yıllar içerisinde dost olmuş mafya adamıdır. Sonny, organizasyonda Tommy\'e üst düzey bir mevkî sağlamış ve Tommy ise bu iyiliklerinden ötürü aileye hep çok sadık kalmıştır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">1971 yılı Liberty City\'sinde Sonny, Tommy Vercetti\'yi Harwood\'da bir adamı öldürmesi için görevlendirir. Ancak bu adam, kendisine düzenlenecek bu saldırıdan haberdardır. Tuzağa düşen Tommy, bir adam öldürecek iken çatışmaya girer ve on bir adam öldürür; bu nedenle 15 yıl&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">cezaevinde</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;yatar. Bu olaydan sonra \"Harwood Canisi\" lakabını alan Tommy, bir an Sonny\'in kendisini kasti olarak tuzağa düşürdüğü konusunda kuşku duyar. Tommy\'nin cezaevinde olduğu dönemde Sonny, rakip ailelerin; üst düzey üyelerinin çoğunu öldürtmüş ve ailesinin gücüne güç katmıştır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Yıllar geçer ve Sonny, büyük paraların döndüğü&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">uyuşturucu</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;işine girmek ister.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">Vice City</span><span style=\"font-family: Helvetica; font-size: 14px;\">, uyuşturucu piyasası için çok uygun bir şehirdir ve Sonny, diğer mafya ailelerinden önce davranarak sektöre hakim olmak ister. İşlerini yapması için sadık birine ihtiyaç duyar ve hapisten çıkacak olan eski sadık dostu Tommy ile tekrar arasını yapmak ister. Tommy\'nin yaptığı katliam&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">Liberty City</span><span style=\"font-family: Helvetica; font-size: 14px;\">\'de unutulmadığından dolayı Sonny, Tommy\'i Vice City\'e gönderir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Sonny\'den aldığı ilk görevde, kimliği belirsiz kişiler tarafından, buluşma yeri olan limana baskın yapılır. Tommy, o baskından, şans eseri kurşunların hedefi olmadan kurtulur. Sonny, milyonlarca&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">dolar</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;parasını ve uyuşturucu ürünlerini, baskında kaybettiği için çok öfkelenir. Öfkeli Sonny, Tommy\'e, parayı ve ürünü çalan hırsızları bulması ve tekrar geri getirmesi için görev verir. İşte tam bu görevden sonra Tommy\'nin Vice City macerası tam anlamıyla başlamış olur.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Gelişme\" style=\"font-family: Helvetica; font-size: 20px;\"><b>Gelişme</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Tommy ilk olarak Albay Cortez\'in partisine katılarak şehrin birçok ileri gelenlerini tanıma fırsatı bulur. Aynı zamanda Tommy, Albay Cortez\'in birçok işini yaparak yardımda bulunur. Albay ise bu yardımlar karşılığında Tommy\'e bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">tekne</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;hediye eder. Öte yandan Tommy, uyuşturucu ticareti sırasında kardeşini kaybetmiş olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">Lance Vance</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;ile tanışır. Tommy ile Lance, partiye katılan ve şehrin önemli mafya babalarından biri olan Diaz için çalışmaya başlar. Zamanla limanda yapılan baskının failinin Diaz olduğu ortaya çıkınca Tommy ve Lance, Diaz\'ın mekanını basarak Diaz\'ı öldürür ve malikaneyi ele geçirir. Bu olaydan sonra Tommy, şehirdeki itibarını iyice büyütmeye başlar. Öyle ki kendisine haraç vermeyen mağazaların gözünü korkutarak iktidarlığını pekiştirir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Tommy, şehrin batısında Haititiler ile savaş halinde bulunan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">Kübalıların</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;lideri Umberto Robino ile karşılaşır. Tommy\'nin sağlam adam olduğunu fark eden Robino onla işbirliği yapmak ister. Tommy, Umberto\'nun tuhaf kişiliğine rağmen istifini bozmayarak ona bazı konularda yardımcı olur. Lakin Kübalılar ile birlikte Haitililerin bir fabrikasını havaya uçurması, Haitililerin Vercetti iktidarlığına kalıcı savaş açmasına sebep olur. Aynı zamanda bu bölgelerde bulunan bir taksi durağı ile dondurmacı dükkanı satın alarak servetini yükseltir. İleriki zamanlarda ise şehrin kuzeyindeki film stüdyosunu ve Malibu kulübünü de satın alarak buradan yüklü miktarda para kaldırır. En son Malibu Kulübünü de satın alarak burada kurduğu dört kişilik bir ekip ile bir bankayı soyar ve bu soygundan istediğini elde eder. Bütün bu olaylar Tommy Vercetti\'yi, Vice City\'nin en saygın ve itibarlı adamlarından biri yapar.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Sonny, uzun bir zaman geçmesine rağmen halen parasını alamadığı için sürekli Tommy\'e telefon açarak baskı kurmaya çalışır. Tommy\'nin bu denli büyümesinden pek hoşlanmayan Sonny, borcunun kapanması için Tommy\'nin işlerinden pay almak ister. Sonny, adamlarını tahsilat için Vice City\'ye gönderir.Sonny\'nin adamları,Tommy için manevi değeri çok büyük olan matbaasını basarak çalışanını yaralar. Bu olaydan sonra küplere binen Tommy hepsini öldürür. Bunun üzerine Sonny, Tommy\'nin partneri Lance\'e yaklaşır ve ikisi anlaşmaya varırlar. Sonny, Vice City\'e gelerek Tommy\'ye ziyarette bulunur. Tommy sahte paralar ile Sonny\'e olan borcunu ödemeyi planlanmaktadır. Tommy, Sonny ve adamlarını malikanesinin kapısında karşılar. Aralarında geçen kısa konuşmadan sonra Tommy, Sonny‘ye sahte para vermeye kalkışır. Bunu bilen Lance, Sonny\'yi uyarır ve Tommy işte o zaman kendisini tuzağa düşüren kişinin Sonny olduğuna emin olur. Sonny kendisini aldatmaya çalıştığı için Tommy\'nin konağına adamları ile saldırır. Konakta büyük bir çatışma çıkar. Tommy, hem Lance\'i hem de Sonny\'i öldürür. Bu olayda birçok üyesini kaybeden Forelli ailesi çöküş dönemine girer. Bu da Sindacco ailesi gibi mafya aileleri için olumlu bir gelişme olmuştur.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Sonuç\" style=\"font-family: Helvetica; font-size: 20px;\"><b>Sonuç</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Vercetti, Vice City\'deki en güçlü ve zengin suç lideri olur. Lance Vance ve Sonny Forelli öldü; ve Forelli ailesi çöküş dönemine girdi. Daha sonraki yıllarda Tommy\'nin gücünün ve saltanatının ne kadar sürdüğü Rockstar Games tarafından senaryosu yazılmadığı için tam olarak bilinmemektedir. Ancak 1992\'ye kadar yaşadığı Ken Rosenberg\'in (Grand Theft Auto: San Andreas The Introduction videosunun bir sahnesi) Las Venturas\'taki bir rehabilitasyon kliniğinden Tommy\'e çağrı atması geleceğe dair bir ipucu olarak gösterilebilir. Rosenberg\'ün telefonlarına Tommy\'nin cevap vermemesi ve adamlarından birinin Rosenberg\'ü geri çevirmesi, Tommy ile Ken arasındaki ilişkinin bittiğini vurgular.</span></p>', 1, 251, '/uploads/games/grand-theft-auto-vice-city/grand-theft-auto-vice-city-cover.png', '/uploads/games/grand-theft-auto-vice-city/grand-theft-auto-vice-city-1.jpeg', '/uploads/games/grand-theft-auto-vice-city/grand-theft-auto-vice-city-2.jpg', '/uploads/games/grand-theft-auto-vice-city/grand-theft-auto-vice-city-3.jpg', '/uploads/games/grand-theft-auto-vice-city/grand-theft-auto-vice-city-4.jpg', '/uploads/games/grand-theft-auto-vice-city/grand-theft-auto-vice-city-5.jpg', '/uploads/games/grand-theft-auto-vice-city/grand-theft-auto-vice-city-6.jpg', '/uploads/games/grand-theft-auto-vice-city/grand-theft-auto-vice-city-7.jpg', 'https://www.youtube.com/embed/f_VBXRZuHTc', 'https://www.youtube.com/embed/N9YvopAJEgo', 'https://www.youtube.com/embed/woGHDe7WMlQ', 'https://www.youtube.com/embed/HZE-Mc4s8k8', 'https://www.youtube.com/embed/O9cNRvyuAes', NULL, '2021-12-28 09:12:02', '2022-02-16 11:20:49'),
(2, 'Battlefield 2042', 2, 3, 2, 2, 2, 2, 'battlefield-2042', 'Battlefield 2042, video oyunu geliştiricisi Electronic Arts\'ın yayıncılığını yaptığı ve video oyunu geliştiricileri; DICE ile Criterion Games tarafından geliştirilen bir birinci şahıs nişancı video oyunudur.', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Battlefield 2042,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">video oyunu geliştiricisi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Electronic Arts</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın yayıncılığını yaptığı ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">video oyunu geliştiricileri</span><span style=\"font-size: 14px; font-family: Helvetica;\">;&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">DICE</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Criterion Games</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından geliştirilen bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">birinci şahıs nişancı</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">video oyunudur</span><span style=\"font-size: 14px; font-family: Helvetica;\">. 2042 yılındaki \"insanlık için savaş\" sırasında geçen kurgusal bir hikâye ile 2013\'te çıkan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Battlefield 4</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ün doğrudan devamı niteliğindedir. Oyun, 19 Kasım 2021\'de</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 4</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 5</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox Series X/S</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;için piyasaya sürüldü.</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Doğu Asya</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Orta Doğu</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli çeşitli yeni haritalar içeren&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">fragman</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">, 9 Haziran 2021\'de&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">YouTube\'da</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;yayınlandı.</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;Oyun içi oynanış görüntüleri ise 13 Haziran 2021\'de yayınlandı.</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;Oyunda tek kişilik hikâye modu bulunmayacak, onun yerine yapımcı&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">DICE</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çok oyunculu</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;moduna odaklanacak.&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Çok oyunculu</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;maçlarını isteğe bağlı olarak yapay zekalı botlar ile oynamak mümkün olacak.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Oynanış\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Oynanış</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyunda haritalar dinamik bir hava durumuna sahip olacak ve maç içerisinde, kum fırtınası veya fırtına gibi olaylar görülecek. En son&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Battlefield 4</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'te ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hardline</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da görülen \"levolution\" (belirli koşulların gerçekleşmesiyle büyük bir yıkımla haritanın değişmesi) özelliği geri dönüyor. Maçlar 128 oyuncu olarak 64 vs 64 olarak yapılacak ve haritalar devasa bir alanda farklı farklı kapışma imkânları sağlayacak, ancak eski nesil konsollarda oyuncular, 32 vs 32 toplam harita limiti 64 kullanıcı olacak şekilde maçlar yapabilecek ve ona uygun haritalarda oynayabilecekler.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Roketin fırlatılma tehlikesine karşı mücadele edeceğiniz Orbital (Fransız Guyanası), etrafı kumlarla çevrilerek kaybolan bir şehir olan Hourglass (Katar), plazaların yükseldiği Kaleidoscope (Güney Kore), bir limanda kapışacağınız Manifest (Singapur), ömrünü tamamlamış gemilerin hurdalarını içeren Discarded (Hindistan), Antarktika\'nın buzlarında mücadele vereceğiniz Breakaway ve Mısır\'da geçecek olan Renewal, ilk duyrulan haritalar oldu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">\"Specialist\"ler oyuna yeni eklenen içeriklerden biri olacak. Henüz nasıl kullanılacağı gösterilmeyen ve özel yetenekleri olan bu karakterleri seçip mücadele edilebilecek. Hazard Zone isminde yeni bir mod eklenecek, şu anlık resmî bir bilgi bulunmuyor, ancak uzun zamandır iddiaları dolaşan battle royale modunun ismi olabilir. Oyun, live service modeliyle (oyunun çıkışı sonrası düzenli eklentilerle oyunun ilerletilmesi) yönetilecek, hatta ilk yıl için pass çıkarılacağı ve bu pass sahiplerine 4 yeni operatör verileceği söylendi.</span></p>', 1, 57, '/uploads/games/battlefield-2042/battlefield-2042-cover.jpg', '/uploads/games/battlefield-2042/battlefield-2042-1.png', '/uploads/games/battlefield-2042/battlefield-2042-2.png', '/uploads/games/battlefield-2042/battlefield-2042-3.jpg', '/uploads/games/battlefield-2042/battlefield-2042-4.jpg', '/uploads/games/battlefield-2042/battlefield-2042-5.jpg', '/uploads/games/battlefield-2042/battlefield-2042-6.jpg', '/uploads/games/battlefield-2042/battlefield-2042-7.jpg', 'https://www.youtube.com/embed/ASzOzrB-a9E', 'https://www.youtube.com/embed/WomAGoEh-Ss', 'https://www.youtube.com/embed/dA6DwKBtAZ0', NULL, NULL, NULL, '2022-01-03 14:29:00', '2022-02-16 11:11:20');
INSERT INTO `games` (`id`, `name`, `category_id`, `developer_id`, `publisher_id`, `sys_req_min_id`, `sys_req_rec_id`, `game_details_id`, `slug`, `sub_title`, `description`, `status`, `hit`, `cover_image`, `image1`, `image2`, `image3`, `image4`, `image5`, `image6`, `image7`, `video1`, `video2`, `video3`, `video4`, `video5`, `deleted_at`, `created_at`, `updated_at`) VALUES
(9, 'Metro Exodus', 2, 4, 3, 14, 14, 14, 'metro-exodus', 'Metro Exodus, 4A Games tarafından geliştirilen ve Deep Silver tarafından yayınlanan birinci şahıs nişancı tarzında video oyunudur.', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Metro Exodus,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">4A Games</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından geliştirilen ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Deep Silver</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından yayınlanan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">birinci şahıs nişancı</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarzında&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunudur</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Yazar&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Dmitry Glukhovsky</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin romanlarından esinlenen Metro oyun serisinin üçüncü oyunudur.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro 2033</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro: Last Light</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunlarında yaşanan hadiseleri izlemektedir.&nbsp;Metro Exodus: The Two Colonels&nbsp;ve&nbsp;Metro Exodus: Sam\'s Story&nbsp;genişleme paketleriyle hikâye genişlemiştir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Oynanış\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Oynanış</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Metro Exodus&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">hayatta kalma-korku</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">gizlilik</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;öğeleri barındıran&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">birinci şahıs nişancı</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarzında bir oyundur. Yıkım sonrası&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Rusya Federasyonu</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun belli şehirlerinde ve ağırlıklı olarak kırsal bölgelerinde geçen oyunda, oyuncuyu yeni tehlikeler ve yeni düşmanlar beklemektedir. Serinin ilk iki oyunundan farklı olarak oyuncunun çevreden topladığı parçalar marifetiyle silahlarını modifiye edebileceği zanaatkarlık sistemi bulunmaktadır. Oyun yarı açık bir dünyada (bölüm bazlı) lineer bir oynanış sunmaktadır. Dinamik hava durumu sistemine sahip oyunda ayrıca gece gündüz döngüsü bulunmakta ve hikâye ilerleyişine göre mevsimler ve çevre değişmektedir. Oyun bir yıllık zaman diliminde geçmektedir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\".C3.96zet\" style=\"\"></span><span class=\"mw-headline\" id=\"Özet\" style=\"font-size: 24px; font-family: Helvetica;\">Özet</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Metro Exodus,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro: Last Light</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ta yaşanan hadiselerden sonra&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">nükleer savaşın</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yıkıma uğrattığı post apokaliptik yeryüzünde geçmektedir. Oyun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro:Last Light</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın \"Kefaret\" sonundan (iyi son) hikâyeye devam eder. İlk iki oyunda olduğu gibi oyuncu&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Artyom</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;rolünü üstlenir ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Moskova Metrosu</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ndan kaçarak Spartalı Korucularla birlikte ülkenin uzak doğusuna yolculuğa çıkar. Artyom ve arkadaşları, Aurora adını koydukları lokomotifle önce&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Ural Dağları</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'na çok da uzak olmayan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Volga Nehri</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;civarına yolculuk eder. Daha sonra yeni bir hayat kurmak için doğuya doğru yol alırlar.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Moskova Metrosunun</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;sert nükleer kışıyla başlayan hikâye bir yıllık süreçte cereyan eder. Diğer ana karakter, bir önceki oyundan ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro 2035</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kitabından hikâyeye geri dönen ve şu anda Artyom\'un eşi olan Anna\'dır.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\"Sam.27s_Story\" style=\"\"></span><span class=\"mw-headline\" id=\"Sam\'s_Story\" style=\"font-size: 22px; font-family: Helvetica;\">Sam\'s Story</span></b></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bombalar patlamadan önce Moskova elçiliğinde görevli bir Amerikan deniz piyadesi olan Sam uzun süredir ana vatanına dönmeyi hayal ediyordu. Metro\'nun ıssız ve karanlık tünellerinde bu hayal imkânsız gibi görünüyordu ta ki Moskova\'nın dışında yaşayan insanların olduğunu öğrenene kadar. Spartalı Yoldaşlığı, Moskova\'nın hayatta kalan tek şehir olmadığını öğrenince Sam\'e artık bu imkânsız olarak gelmiyordu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sam, Vladivostok\'un yıkık ve dökük binalarından, Aurora\'dan uzaklaşarak evine doğru yola çıkar. Her şeyin beklediğinden çok daha zor olduğunu gören Sam bu uzun yolculukta hayatta kalmak için öğrendiği tüm tecrübe ve bilgilerine ihtiyacı olduğunu keşfedecek.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\"Olay_.C3.96rg.C3.BCs.C3.BC\" style=\"\"></span><span class=\"mw-headline\" id=\"Olay_Örgüsü\" style=\"font-size: 22px; font-family: Helvetica;\">Olay Örgüsü</span></b></h3><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro: Last Light</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ta yaşanan D6 saldırısının ardından Artyom, metrodaki dur durak bilmeyen iç çatışmalardan ve yozlaşmadan hayal kırıklığına uğrar ve eşi Anna ile birlikte&nbsp;Spartalı Nizamı\'ndan ayrılır. Çok geçmeden Moskova dışında hayatta kalanların var olduğunu ispatlamayı takıntı haline getiren Artyom, eşi Anna\'nın kaygılarına ve Miller\'ın hoşnutsuzluğuna rağmen yüzeye sayısız tehlikeli keşif yolculuğu yapar. Yaptığı tehlikeli yolculukların birinden dönerken Spartalı arkadaşları&nbsp;Duke,&nbsp;Damir,&nbsp;Idiot&nbsp;ve&nbsp;Alyosha&nbsp;tarafından kurtarılmadan az önce Artyom,&nbsp;Gözcü&nbsp;mutant sürüsü tarafından feci halde hırpalanır. Gözünü hastanede açar. Başında endişeyle bekleyen eşi Anna, artık bu tehlikeli yolculuklardan vazgeçmesi için yalvarır ve ona yaptığı her yolculuğun kendisini daha fazla radyasyona maruz bırakarak sağlıklı bir evlat sahibi olma şanslarını azalttığı hakikatini dile getirir. Miller ikilinin Spartalı Nizamı\'na eğitici olarak geri dönmeleri gerektiğini ve Artyom’un dış dünyayla temas kurma girişimlerinin umutsuz fanteziler olduğunu savunur. Bu olaydan bir müddet sonra her nasılsa Anna’yı ikna eden Artyom, eşini de yanına alarak dış keşif gezisine çıkar. İkili bu geziden dönerken yüzeyde çalışan bir trenin önlerinden geçip gittiğine şahit olurlar. Takip edemeden Hansa askerleri tarafından yakalanırlar. Bindikleri araçta Moskova dışından olduklarını ifşa eden iki tutsak daha vardır. Hansa askerleri bu iki yabancıyı yarı yolda indirip infaz etmek isterken araya giren Artyom vurulur ve ikiliyle birlikte düştüğü çukurda ölüme terk edilir. Hayatta kalan Artyom, askerleri üslerine kadar gizlice takip eder. Anna’yı kurtarmayı başardıktan sonra kaçış sürecindeki bir boğuşma esnasında, yanlışlıkla&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Moskova</span><span style=\"font-size: 14px; font-family: Helvetica;\">’nın dış dünyayla iletişimini engelleyen sinyal bozucuyu tahrip eder. Bütün dünyadan radyo iletişim sinyalleri gelmeye başlar ve ikili insanlığın neslinin tükenmediğini idrak eder. Hansa için çalışan tren mühendisi&nbsp;Yermak, ikiliye yardım etmeyi kabul eder ve&nbsp;Hansa&nbsp;trenlerinden birini çalarak kaçmayı başarırlar.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ayrılacakları esnada Spartalı Nizamı treni geri almak için harekete geçer. Baskın yaptıkları anda Artyom ve Anna’yı karşılarında bulmanın şokunu yaşarlar. Anna babasını Hansa\'yla işbirliği yaparak gerçekleri örtbas etmekle suçlayınca hepten kafaları karışır. Sırrı öğrendikleri için Hansa’nın kendilerini yaşatmayacağının farkında olan Miller hayatta kalmak için en iyi ihtimalin Moskova’dan kaçış olduğuna hükmeder. Duke, Damir, Idiot ve Alyosha, Miller’ın şahsi koruması eski bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Amerikan Deniz Piyadesi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;olan&nbsp;Sam, ağır silah uzmanı&nbsp;Stephan&nbsp;ve usta silah yapımcısı&nbsp;Tokarev&nbsp;de albaya katılırlar ve takım yola koyulur. Kaçışları sırasında Hansa’ya ait zırhlı bir treni yok ettikleri için Hansa tarafından resmen hain ilan edilirler. Şehirden ayrıldıktan bir süre sonra Miller ekibe&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Rusya</span><span style=\"font-size: 14px; font-family: Helvetica;\">’nın birçok şehrinin bombalandığını, savaşın henüz bitmediğini,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">NATO</span><span style=\"font-size: 14px; font-family: Helvetica;\">’nun uluslarından geriye kalan ne varsa işgale devam ettiğini ifşa eder. Moskova’nın daha fazla bombalanmasını önlemek maksadıyla Rus hükumetinin gizlice bütün iletişimi bozmaya karar verdiğini, böylelikle dış dünyayı şehirde kimsenin hayatta kalmadığına inandırmayı amaçladığını anlatır. O sırada&nbsp;Moskova Savunma Komutanlığı’nın hayatta kalanları&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Yamantau Dağı</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nda yer alan&nbsp;Ark&nbsp;üssünde toplanmaya çağıran radyo yayınını alırlar. Miller oraya gitmeye karar verir. Ark’ın Rus hükumetinin kendini yeniden inşa ettiği yer olduğuna inanmaktadır ve Moskova’da hala hayatta kalanların olduğunu yetkililere haber vererek Hansa’nın ölüm cezasından da affedilmelerini umar. Biraz münazaradan sonra ekip trene&nbsp;Aurora&nbsp;adını koyar. Spartalılar Yamantau yolunda ilk engelleriyle&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Volga Nehri</span><span style=\"font-size: 14px; font-family: Helvetica;\">’ni geçerken karşılaşırlar. Geçmeleri gereken köprü ve civar mıntıka&nbsp;Silantius&nbsp;adında liderleri olan elektrikli ne varsa günah sayan mutaassıp fanatiklerin kontrolü altındadır.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Fanatiklerle</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ilk kontağı kurmak için giden Artyom kendisine kurulan tuzaktan kaçmayı başarır ve kültün tutsağı olan&nbsp;Katya&nbsp;adında bir kadınla küçük kızı&nbsp;Nastya’nın kurtulmasına yardım eder. Artyom, Anna ve Stephan’ın ikna çabalarından sonra Miller, bir sıhhiye olan Katya’nın kızıyla birlikte gruba katılımına izin verir. Grup aynı zamanda terk edilmiş bir limanda kültten ve bölgenin haydutlarından uzak yaşamaya çalışan&nbsp;Krest&nbsp;adında bir mekanikle de kontak kurar. Artyom limana gider ve Krest’le görüşerek kendisini ekibe dahil eder. Sonunda ekip başarılı bir operasyonla ele geçirdikleri bir ticaret gemisiyle köprüye sızar ve Aurora’nın geçişi için köprüyü indirirler. Bir müddet yolculuğun ardından sonra grup Yamantau’ya varır ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Rus</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;hükûmetinden arda kalanların&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yamyamlaştığını</span><span style=\"font-size: 14px; font-family: Helvetica;\">, hayatta kalan insanları sahte güvenli sığınak yayınıyla üsse çektiğini keşfetmeleri uzun sürmez. Artyom, Anna ve Miller ele geçirilir. Sam ve Alyosha, Artyom ve Miller’ı kurtarır. Anna gruptan ayrı bir yere götürüldüğünden Artyom kurtarmaya gider. Yol boyunca çatışarak Anna’ya ulaşır. Spartalılar bu hengameyi de atlatarak üsten kaçmayı başarırlar. Lakin son affedilme ve Metro’ya dönüş umutları da suya düşer. Miller’ın yeni Rusya inancı paramparça olmuş ekip şimdi ne yapmak gerektiğini düşünekalmıştır. Yamantau’dan kurtardıkları bir haritayı kullanarak Hazar Denizi yakınındaki uydu iletişim merkezine gitmeye karar verirler. Yaşama elverişli bir yer bulup yerleşmek için radyoaktif serpinti sahalarının uydu kayıtlarına ulaşmayı ümit ederler. Çölleşmiş&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hazar Denizi</span><span style=\"font-size: 14px; font-family: Helvetica;\">’nde, Spartalılar yerel halkı köleleştirmiş&nbsp;Baron&nbsp;namında bir adamın idaresindeki haydutlara rastlarlar. Yerel halktan bir kadın olan&nbsp;Gül’ün de yardımlarıyla Spartalılar aşırı ihtiyaç duydukları su ve yakıtı haydutlardan çalmayı başarırlar. Kölelerini özgür bırakırlar ve Baron’u öldürürler. Bütün bunların yanında güncel bir uydu haritası da elde ederler. Spartalıların yardımlarına minnettar olsa da Gül, geride kalıp arda kalan haydutlarla savaşmayı sürdürmeyi seçer.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Spartalılar yolculuklarına devam ederken, Stephan Katya’ya evlilik teklifinde bulunur. Aurora’daki düğün merasimi esnasında Anna kanlı öksürmeye başlar, Volga’da kazara düştüğü sığınakta soluduğu zehirli gazdan kaynaklandığından kuşkulanmaktadır. Ark\'ta karşılaştıkları korkunç doktorun ciğerlerinin kötüye gittiğinden bahsettiği gerçeğini paylaşır. Miller hemen itiraz ederek, doktorun güvenilmez olduğunu vurgular ve Moskova kışından kumlu çöle anlık iklim değişikliğini neden gösterir. Artyom ve Katya\'yı özel görüşmek için lokomotife çağırır. Katya da Miller ile aynı fikirdedir ve gitmekte oldukları yerin orman havasının bile kendi başına Anna\'yı iyileştirmeye yetebileceği görüşünde bulunur. Miller\'ın eğer doktor haklıysa sorusuna ise savaşın eşiğinde bir ilaç geliştirildiğini ve gaz maruziyeti ve genel zehirlenmelerden birçok insanı kurtardığını anlatır. İlacın üretim yerinin hatırladığı kadarıyla&nbsp;Novosibirsk&nbsp;olduğunu iletir. Yerleşmek için vardıkları vadi ormanı keşfe çıkmaya karar veren ekip Artyom ile Alyosha’yı öncü gönderirler. Toprak kayması yüzünden otorayları raydan çıkarak nehre yuvarlanır. Nehre düşen ikili birbirinden ayrılır. Yöreden&nbsp;Olga&nbsp;adında bir kadının yardımıyla Artyom, Alyosha’yı bulmaya çalışırken hayatta kalan iki düşman grup arasında kalır. İki grup da&nbsp;Ormanın Efendisi&nbsp;adında devasa bir mutant ayı tarafından sürekli terörize edilmektedir. Diğer taraftan Artyom, vadiyi radyasyondan koruyan barajın bölgeyi yerleşime elverişsiz kılacak derecede yıkılmanın eşiğinde olduğunu da keşfeder. Neticede Artyom ayıyı öldürür ve Olga’ya âşık olmaktan kendini alamayan Alyosha’yı da yanına alarak trene geri döner. Anna’nın durumunun daha da kötüleştiğini öğrenir. Anna’yı kurtarmanın tek yolu Novosibirsk’te Katya\'nın bahsettiği panzehiri bulmaktır. Fakat grup uydu haritasını tetkik ederken bütün şehrin Moskova’dan çok daha ağır bir şekilde radyasyon altında olduğunu fark eder. Kışın hızla yaklaşması ve başka seçeneklerinin olmamasından ötürü, Miller ve Artyom bütün riski göze alarak yanlarına kimseyi almadan panzehiri aramak için şehre gitmeye karar verir. Bu sırada ekibin kalanı Aurora’ya kar küreyici takmak için yola çıkar. İkili Novosibirisk metrosuna girerler ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Novosibirsk</span><span style=\"font-size: 14px; font-family: Helvetica;\">’te son hayatta kalanlardan biri olan&nbsp;Kiril&nbsp;adında küçük bir çocuk bulurlar. Kiril babasının yaşama elverişli bir bölgenin yerini gösterir bir haritayı ele geçirmek için çıktığı bir görevde kaybolduğunu anlatır. Miller haritayı bulmaya karar verirken, Artyom tek başına panzehiri bulmak için yola koyulur. Şehrin daha derinlerine ilerledikçe Anna’nın canlı halüsinasyonlarını görmeye başlar ve&nbsp;Kör Olanlar&nbsp;olarak bilinen bir takım devasa goril-imsi mutantlarla mücadele eder. Panzehiri kurtarmayı başaran Artyom Kör Olanlardan biri tarafından ağır bir şekilde yaralanır ve aşırı dozda radyasyona maruz kalır. Artyom, Miller ve Kiril harita ve panzehirle trene dönerler fakat Miller dönüş yolunda maruz kaldığı aşırı radyasyon zehirlenmesinden ölür. Spartalıların geri kalanı Artyom’u kurtarmak için kanlarını bağışlarlar. Anna panzehirle iyileşir ve haritayı takip eden Spartalılar radyasyondan arınmış&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Baykal Gölü</span><span style=\"font-size: 14px; font-family: Helvetica;\">’nün kıyısına yerleşir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ancak Artyom’un nihai kaderi yolculuk boyunca aldığı kararlara dayanmaktadır. Kötü sonda, Artyom radyasyon zehirlenmesinden ölür ve Spartalılarla birlikte kederli Anna, babası ve onun için cenaze merasimi düzenler. İyi sonda, Artyom hayatta kalmayı başarır. Miller gömülür ve Artyom yerini alarak Spartalı Nizamı’nın liderliğine seçilir. Artık evleri olduğuna göre Artyom diğer hayatta kalanları bulma vaktinin geldiğine karar verir. Oyuncunun seçimlerine göre ekipten birkaçının kaderi de değişmektedir. Eğer çok fazla kötü veya çok az iyi karar verilirse, Duke Volga’da köprüyü indirirken öldürülecek, Damir halkını haydutların elinden kurtarmak isteyen Gül’e yardım etmek için geride kalacak ve Alyosha vadi ormandan kaçarken ciddi şekilde yaralanacak. Bütün bu yaşananlar oyunun sonunda Artyom’a kan verecek Spartalı Korucu sayısına etki edecek.</span></p>', 1, 74, '/uploads/games/metro-exodus/metro-exodus-cover.jpg', '/uploads/games/metro-exodus/metro-exodus-1.jpg', '/uploads/games/metro-exodus/metro-exodus-2.jpg', '/uploads/games/metro-exodus/metro-exodus-3.jpg', '/uploads/games/metro-exodus/metro-exodus-4.jpg', '/uploads/games/metro-exodus/metro-exodus-5.jpg', '/uploads/games/metro-exodus/metro-exodus-6.jpg', '/uploads/games/metro-exodus/metro-exodus-7.jpg', 'https://www.youtube.com/embed/fbbqlvuovQ0', 'https://www.youtube.com/embed/L_LpGTtCzbw', NULL, NULL, NULL, NULL, '2022-01-04 08:30:42', '2022-02-23 12:42:55'),
(11, 'Age Of Empires IV', 3, 6, 5, 16, 16, 16, 'age-of-empires-iv', 'Age of Empires IV, Relic Entertainment tarafından geliştirilen ve 28 Ekim 2021\'de piyasaya çıkan bir gerçek zamanlı strateji video oyunudur. Age of Empires serisinin oyunudur.', '<h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Özet\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Özet</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Orta Çağ</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da geçer. İngiltere\'nin Normanlar tarafından fethini anlatan bir sefer de mevcut</span><span style=\"font-size: 14px; font-family: Helvetica;\">.&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">Age of Empires IV ile en sevilen gerçek zamanlı strateji oyunlarından biri, sizi dünyayı şekillendiren destansı tarihi savaşların merkezine koyarak tüm ihtişamıyla geri dönüyor. Büyüleyici 4k görsel uyumluluğu ile uçsuz bucaksız manzaralarda imparatorluğunuzu genişletmek için hem tanıdık hem de yenilikçi yollar sunan Age of Empires IV, yeni bir nesle gelişmiş bir gerçek zamanlı strateji oyunu sunuyor.</span></p><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Tarihe Dönün – Zafer arayışınızda İngilizlerden Çinlilere ve Delhi Sultanlığı\'na kadar dünya çapında 8 farklı medeniyetin zengin tarihine daldığınız zaman geçmiş sizin için henüz bir başlangıç olacak. Karanlık Çağlardan Rönesans\'a kadar 500 yıllık bir tarihe yayılan 35 görevle 4 farklı seferde şehirler inşa edin, kaynakları yönetin ve birliklerinizi karada ve denizde savaşa yönlendirin.</span><br style=\"padding: 0px; margin: 0px;\"><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Tarihi Şahsiyetlerle Yüceliğe Giden Yolunuzu Seçin - Jeanne d\'Arc\'ın İngilizleri mağlup etme yolundaki maceralarını yaşayın veya Cengiz Han olarak kudretli Moğol birliklerini komuta ederek Asya\'yı fethedin. Seçim sizin – Yapacağınız her seçim tarihin akışını belirleyecektir.</span><br style=\"padding: 0px; margin: 0px;\"><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyununuzu Modlarla Özelleştirin – 2022\'nin başlarında sunulacak olan özel oyunlar için kullanıcı tarafından oluşturulmuş içerik araçlarıyla dilediğiniz gibi oynayın.</span><br style=\"padding: 0px; margin: 0px;\"><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Dünyaya Meydan Okuyun- Çevrimiçi olun ve 7 kişiye kadar arkadaşlarınızla PVP ve PVE çok oyunculu modlarda rekabet edin, iş birliği yapın veya oyun izleyin.</span><br style=\"padding: 0px; margin: 0px;\"><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Tüm Oyunculara Uygun Bir Çağ – Age of Empires IV, gerçek zamanlı stratejinin esaslarını öğreten bir eğitim sistemi ve ilk kez oynayacakların kolay bir kurulum ve başarı elde etmelerine yardımcı olması için tasarlanmış bir Sefer Hikâye Modu ile yeni oyuncular için cazip bir tecrübedir. Fakat aynı zamanda da yeni oyun mekanikleri, gelişmiş stratejiler ve çarpışma teknikleriyle deneyimli oyuncular için de tatmin edici şekilde zorlu bir deneyim sunar.</span><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Oynan.C4.B1.C5.9F\" style=\"\"></span><span class=\"mw-headline\" id=\"Oynanış\" style=\"font-size: 24px; font-family: Helvetica;\">Oynanış</span></b></h2><p style=\"margin: 0.5em 0px; line-height: 1.2;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Çoğu oyun mekaniği doğrudan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Age of Empires II</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'den&nbsp;(serideki en başarılı oyun) taşınacaktır. Örneğin, taş yeniden kaynaklardan birisi olacak, Monks (Keşişler) birimleri dönüştürebilecek.</span></p><p style=\"margin: 0.5em 0px; line-height: 1.2;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyunda 8 adet oynanabilir medeniyet bulunuyor.&nbsp;Bu medeniyetler:&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İngilizler</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Çinliler</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Moğollar</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Delhi Sultanlığı</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Fransızlar</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Abbâsîler</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kutsal Roma İmparatorluğu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Moskova Knezliği</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'dir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Geli.C5.9Ftirme\" style=\"\"></span><span class=\"mw-headline\" id=\"Geliştirme\" style=\"font-size: 24px; font-family: Helvetica;\">Geliştirme</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">21 Ağustos 2017\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Relic Entertainment</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından geliştirilen&nbsp;Age of Empires IV\'ü&nbsp;duyurdu.&nbsp;Microsoft\'un Oyunlardan Sorumlu Başkan Yardımcısı Phil Spencer, 11 Haziran 2019\'da&nbsp;Age of Empires IV\'ün&nbsp;hala geliştirilmekte olduğunu doğruladı.&nbsp;14 Kasım 2019\'da, X019 etkinliğinde&nbsp;Age of Empires IV\'ün&nbsp;oynanış görüntüleri gösterildi.</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;Görüntüler İngiliz ve Moğol kuvvetleri arasındaki ortaçağ savaşını gösteriyordu.</span></p>', 1, 83, '/uploads/games/age-of-empires-iv/age-of-empires-iv-cover.jpg', '/uploads/games/age-of-empires-iv/age-of-empires-iv-1.jpg', '/uploads/games/age-of-empires-iv/age-of-empires-iv-2.jpg', '/uploads/games/age-of-empires-iv/age-of-empires-iv-3.jpg', '/uploads/games/age-of-empires-iv/age-of-empires-iv-4.jpg', '/uploads/games/age-of-empires-iv/age-of-empires-iv-5.jpg', '/uploads/games/age-of-empires-iv/age-of-empires-iv-6.jpg', '/uploads/games/age-of-empires-iv/age-of-empires-iv-7.jpg', 'https://www.youtube.com/embed/QFlVNtGJVDU?enablejsapi=1', 'https://www.youtube.com/embed/5TnynE3PuDE?enablejsapi=1', NULL, NULL, NULL, NULL, '2022-01-10 12:28:17', '2022-02-16 11:09:51'),
(12, 'Days Gone', 1, 9, 6, 17, 17, 17, 'days-gone', 'Days Gone, Bend Studio tarafından geliştirilen ve Sony Interactive Entertainment tarafından PlayStation 4 ve Microsoft Windows için yayınlanan 2019 Aksiyon-Macera, Hayatta kalma, korku video oyunudur.', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Küresel bir salgının başlamasından iki yıl sonra&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">kıyamet sonrası</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Oregon</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da geçen oyunda eski kanun kaçağı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Deacon</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;St. John, karısı Sarah\'nın hala hayatta olma olasılığını keşfeder ve bu da&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Deacon</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ı onu bulmak için bir arayışa sokar. Days Gone, oyuncunun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Açık dünya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ortamını keşfedebileceği üçüncü şahıs perspektifinden oynanır. Oyuncular&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ateşli silahlar</span><span style=\"font-size: 14px; font-family: Helvetica;\">, yakın dövüş silahlar ve doğaçlama silahlar kullanabilir ve düşman insanlara ve Freakers olarak bilinen yamyam yaratıklara karşı savunmak için gizlilik kullanabilirler. Büyük bir oyun mekaniği&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Deacon</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın motosikleti Oyuncu karakterinin birincil ulaşım modu ve mobil envanter olarak kullanılır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Başlangıçta bir 2018 lansmanı için planlanan Days Gone birkaç kez ertelendi. Oyun sonunda 26 Nisan 2019\'da piyasaya sürüldü. Yayınlandıktan sonra,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">açık dünya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yönünü, oynanışını ve Sam Witwer\'ın&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Deacon</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;olarak performansını öven eleştirmenlerden karışık eleştiriler aldı, ancak hikâye, karakter gelişimi eksikliği ve birkaç teknik konu eleştirildi.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sony</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Horizon Zero Dawn</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın ardından&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'a birinci taraf içeriğinin daha fazlasını getirme çabalarının bir parçası olarak, Days Gone 18 Mayıs 2021\'de Windows\'ta yayınlandı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\"><br></span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 24px; font-family: Helvetica;\"><b>ÖNE ÇIKAN ÖZELLİKLER</b></span></p><ul><li style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Helvetica;\">Çarpıcı bir ortam: Pasifik Kuzeybatısı\'nda ormanlardan çayırlara, karlı düzlüklerden çöldeki lav sahalarına kadar her şey güzel olduğu kadar ölümcül de. Milyonlarca yıllık volkanik hareketlerin izini taşıyan pek çok çeşit dağ, mağara ve kırsal kasabayı keşfet.</span></span></li><li style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Helvetica;\">Vahşi karşılaşmalar: Saldırgan çetelerin ve Kaçık sürülerinin kol gezdiği bu topraklarda, hayatta kalmak için özel tuzak, silah ve geliştirilebilir yeteneklerini kullanman gerekecek. Devasa bir bölgedeki en değerli yardımcın olan Başıboş motorunu da unutma.</span></span></li><li style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Helvetica;\">Devamlı&nbsp;değişen bir çevre: Deacon\'ın sadık motosikletine atlayarak hava koşullarıyla büyük ölçüde değişen dinamik bir dünyayı, gece-gündüz döngüsünü ve çevresiyle içinde yer alan insanlara uyum sağlayan ve devamlı evrim geçiren Kaçıkları keşfet.</span></span></li><li style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px;\"><span style=\"font-family: Helvetica;\">Sürükleyici bir hikâye: Deacon St. John derin ve kişisel bir kaybın ardından umut kırıntıları ararken çıkmazlar, ihanet ve pişmanlık dolu güçlü bir hikâyede kaybol. Gündelik olarak verilen hayatta kalma mücadelesinde bizi insan yapan şey ne?</span></span><span style=\"font-size: 14px; font-family: Helvetica;\"><br></span></li></ul>', 1, 20, '/uploads/games/days-gone/days-gone-cover.jpg', '/uploads/games/days-gone/days-gone-1.jpg', '/uploads/games/days-gone/days-gone-2.jpg', '/uploads/games/days-gone/days-gone-3.jpg', '/uploads/games/days-gone/days-gone-4.jpg', '/uploads/games/days-gone/days-gone-5.jpg', '/uploads/games/days-gone/days-gone-6.jpg', '/uploads/games/days-gone/days-gone-7.jpg', 'https://www.youtube.com/embed/FKtaOY9lMvM', 'https://www.youtube.com/embed/99NfMJFaG1Y', NULL, NULL, NULL, NULL, '2022-01-12 17:12:36', '2022-02-16 11:14:21');
INSERT INTO `games` (`id`, `name`, `category_id`, `developer_id`, `publisher_id`, `sys_req_min_id`, `sys_req_rec_id`, `game_details_id`, `slug`, `sub_title`, `description`, `status`, `hit`, `cover_image`, `image1`, `image2`, `image3`, `image4`, `image5`, `image6`, `image7`, `video1`, `video2`, `video3`, `video4`, `video5`, `deleted_at`, `created_at`, `updated_at`) VALUES
(13, 'Forza Horizon 5', 4, 10, 7, 18, 18, 18, 'forza-horizon-5', 'Forza Horizon 5, Playground Games tarafından geliştirilen ve Xbox Game Studios tarafından yayınlanan bir yarış oyunudur.', '<p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Forza</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisindeki beşinci Forza Horizon oyunu ve on ikinci Forza oyunu olacaktır.&nbsp;Oyun,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Meksika</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nın kurgusal bir temsilinde geçmektedir. 9 Kasım 2021\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox Series X ve S</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için piyasaya sürüldü.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Oynan.C4.B1.C5.9F\" style=\"\"></span><span class=\"mw-headline\" id=\"Oynanış\" style=\"font-size: 24px; font-family: Helvetica;\">Oynanış</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Forza Horizon 5&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Meksika</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da geçen açık dünya bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yarış oyunudur</span><span style=\"font-size: 14px; font-family: Helvetica;\">.&nbsp;Oyun, tüm Forza Horizon serisindeki en büyük haritaya (</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Forza Horizon 4</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ten %50 daha büyük) ve en yüksek tepe noktasına sahiptir.&nbsp;Harita, yaratıcı yönetmen Mike Brown tarafından ekibin oluşturduğu en çeşitli Forza Horizon haritalarından biri olarak tanımlanmıştır.&nbsp;Harita, aktif bir kaldera yanardağı, ormanlar ve plajlar, antik Maya tapınakları ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Guanajuato</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi şehirleri içerir. Oyuncular açık dünyayı özgürce keşfedebilir, çok oyunculu yarışlarda rekabet edebilir ve hikaye modunu oynayabilirler. Hem oyunda yer alan arabalar hem de oyuncu karakterleri detaylı bir şekilde özelleştirilebilmektedir.&nbsp;Oyun, seride arabalarda&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ışın izlemeyi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;destekleyen ilk oyundur. (Işın izleme yalnızca ForzaVista\'da mevcuttur)</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun, oyuncuların gözle görülür bir fırtına görebilecekleri yeni bir hava durumu sistemi (yerel hava durumu) sunmaktadır. Meksika, geniş bir rakım aralığına sahip çok geniş bir ülke olduğundan, oyunda aynı anda birden fazla iklim ortaya çıkacaktır. Dört mevsim halen mevcut ancak haritanın dört bir köşesinde farklı etkileri olacak. Örneğin, kurak mevsimde toz fırtınaları ortaya çıkarken, tropik fırtınalar sonbahar mevsimi boyunca meydana gelecek.&nbsp;Bir başka detay da ormanda olacak, ortamın artık havaya tepki verecek. Mesela her yerde yapraklar uçuşacak.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun yepyeni bir Horizon Arcade sunuyor. Horizon Arcade, haritaya yayılmış bir dizi mini çok oyunculu oyundan oluşuyor. Bu oyunlardan birisi Horizon Festival\'in kargo uçağının piñataları düşürdüğü \"Piñata pop\". Bu oyundaki amaç diğer oyuncuların yardımıyla olabildiğince çok pinata patlatmaktır. Oyun ayrıca oyuncuların kişisel tercihlerine bağlı olarak özel oyunlar, yarışlar ve daha fazlasını oluşturabilecekleri bir araç seti olan \"EventLab\"ı da tanıtıyor.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Tüm bunlara ek olarak \"Forza Link\" adlı yeni bir özellik tanıtıldı. Forza Link özelliği Brown\'a göre, oyuncuların mevcut durumlarını izleyen, çevrimiçi olarak diğer oyuncularla bağlantı kurmalarına ve birlikte oynamalarına yardımcı olan bir yapay zeka asistanı. Forza Link, başka bir oyuncudan gelen daveti kabul etmeleri durumunda oyuncuların GPS sistemlerini de bağlayabiliyor.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Efsanevi Horizon Maceranız sizi bekliyor! Dünyanın en harika arabalarından yüzlercesiyle birlikte sınırsız, eğlenceli sürücülük aksiyonuyla Meksika\'nın capcanlı ve her daim gelişmekte olan açık dünya manzaralarını keşfedin.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 22px; font-family: Helvetica;\"><b>Bu, Sizin Horizon Maceranız</b></span><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Meksika\'nın capcanlı ve her daim gelişmekte olan açık dünya manzaraları eşliğinde, dünyanın en iyi araçlarının yüzlercesiyle beraber sınırsız, eğlenceli sürücülük aksiyonuyla dolu nefes kesici seferlere önderlik edin.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 22px; font-family: Helvetica;\"><b>Bu, Çeşitliliklerle Dolu bir Dünya</b></span><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Çarpıcı kontrasta ve güzelliğe sahip bir dünyayı keşfedin. Canlı çölleri, yemyeşil ormanları, tarihi şehirleri, gizli harabeleri, el değmemiş plajları, geniş kanyonları ve karlı kaplı yüksek volkanik dağı keşfedin.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 22px; font-family: Helvetica;\"><b>Bu, Macera Dolu bir Açık Dünya</b></span><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sevdiğiniz aktivitelere katılım yapmanız üzerine sizi ödüllendiren yüzlerce mücadeleyi içeren yoğun bir karşılaşmanın içine dalıverin. Yeni karakterlerle tanışın ve onların Horizon Hikâyelerinin akıbetini belirleyin.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 22px; font-family: Helvetica;\"><b>Bu, Gelişen bir Açık Dünya</b></span><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Meksika\'nın benzersiz, dinamik mevsimleri her hafta dünyayı değiştirirken yükselen toz fırtınaları ve şiddetli tropikal fırtınalar gibi hayranlık uyandıran hava durumu olaylarına şahit olun. Yeni etkinlikler, mücadeleler, koleksiyon eşyaları, ödüller ve keşfedilmeyi bekleyen bölgeler için oyuna sık sık uğrayın. Her sezon biri diğerine asla bezemeyecek şekilde eşsiz olacak.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 22px; font-family: Helvetica;\"><b>Bu, Sosyal bir Açık Dünya</b></span><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Diğer oyuncularla takım oluşturun. Sizi ve arkadaşlarınızı aksiyonun içine çeken ve menüler, yükleme ekranları veya lobiler olmadan eğlenmenizi sağlayan keyifli, üst düzey mücadelelerden oluşan ve ardı ardına devam eden eğlenceli bir serinin tadını çıkarmak için Horizon Arcade\'e girin. Horizon Açık\'ta ve Turlarda yeni arkadaşlar edinin, yeni topluluk hediye paylaşımıyla yaratıcılığınızla oluşturduğunuz eserlerinizi paylaşın.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 22px; font-family: Helvetica;\"><b>Bu, Sizin Açık Dünyanız</b></span><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Özel yarışlar, mücadeleler, gösteriler ve tamamen yepyeni oyun modları yaratmak da dâhil olmak üzere, güçlü, yeni EventLab oyun araç setiyle kendi eğlence anlayışınıza hitap eden eserler oluşturun. Açılır tavanları açma ve kapama imkânı, fren kaliperlerini boyama ve daha fazlasını içeren yeni seçeneklerle araçlarınızı her zamankinden daha çeşitli şekillerde özelleştirin. Toplulukla, oluşturduğunuz özel eserleri paylaşmak için yeni Hediye Paketleri özelliğini kullanın.</span><br style=\"padding: 0px; margin: 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Horizon Maceranıza bugün başlayın!</span><span style=\"font-size: 14px; font-family: Helvetica;\"><br></span></p>', 1, 17, '/uploads/games/forza-horizon-5/forza-horizon-5-cover.jpg', '/uploads/games/forza-horizon-5/forza-horizon-5-1.jpg', '/uploads/games/forza-horizon-5/forza-horizon-5-2.jpg', '/uploads/games/forza-horizon-5/forza-horizon-5-3.jpg', '/uploads/games/forza-horizon-5/forza-horizon-5-4.png', '/uploads/games/forza-horizon-5/forza-horizon-5-5.jpg', '/uploads/games/forza-horizon-5/forza-horizon-5-6.jpg', '/uploads/games/forza-horizon-5/forza-horizon-5-7.png', 'https://www.youtube.com/embed/FYH9n37B7Yw', 'https://www.youtube.com/embed/CPgUk3NZcbI', NULL, NULL, NULL, NULL, '2022-01-13 09:32:51', '2022-03-03 12:57:41');
INSERT INTO `games` (`id`, `name`, `category_id`, `developer_id`, `publisher_id`, `sys_req_min_id`, `sys_req_rec_id`, `game_details_id`, `slug`, `sub_title`, `description`, `status`, `hit`, `cover_image`, `image1`, `image2`, `image3`, `image4`, `image5`, `image6`, `image7`, `video1`, `video2`, `video3`, `video4`, `video5`, `deleted_at`, `created_at`, `updated_at`) VALUES
(14, 'Grand Theft Auto V', 1, 1, 1, 19, 19, 19, 'grand-theft-auto-v', 'Grand Theft Auto V (aynı zamanda Grand Theft Auto Five, GTA 5, GTA V veya GTAV olarak da bilinir), Rockstar North tarafından geliştirilen ve Rockstar Games tarafından yayımlanan 2013 tarihli açık uçlu aksiyon-macera tarzı bir video oyunudur.', '<p style=\"margin: 0.5em 0px;\"><span>﻿</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisinin yedinci ana oyunu olan&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">GTA V</span><font color=\"#202122\" face=\"sans-serif\"><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span></font><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Güney Kaliforniya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ya dayanılarak tasarlanmış olan kurgusal San Andreas eyaletinde geçmektedir ve tek oyunculu hikâyesi, üç&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ana karakterin</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;-eski&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">banka soyguncusu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;Michael De Santa,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sokak çetesi üyesi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;Franklin Clinton ve uyuşturucu satıcısı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Trevor Philips</span><span style=\"font-size: 14px; font-family: Helvetica;\">- yozlaşmış bir devlet kurumuyla güçlü suçluların baskısı altındayken soygun yapma çabalarını konu almaktadır.&nbsp;Modellenen açık dünya, oyuncuların San Andreas\'ın açık kırsalında (Blaine County) ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Los Angeles</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'a dayanılarak tasarlanmış şehir merkezinde (Los Santos) serbestçe dolaşmasına olanak tanımaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">birinci şahıs</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;veya&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">üçüncü şahıs</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;bakış açısıyla oynanabilir; açık dünyasında yürüyerek veya araç içinde yolculuk yapılabilir.&nbsp;Oyuncular, tek oyunculu modundayken üç ana karakteri de kontrol eder ve hem görev sırasında hem de görev dışında aralarında geçiş yapabilir.&nbsp;Hikâye daha çok soygun sekanslarına odaklıdır. Çoğu görevde oyuncuların araç sürmesi ve ateş etmesi gerekmektedir. \"Yıldız\" sistemi, kolluk kuvvetlerinin suç işlemiş olan oyunculara göstereceği tepkiyi ifade etmektedir.&nbsp;Oyunun çok oyunculu modu olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto Online</span><span style=\"font-size: 14px; font-family: Helvetica;\">, -en fazla otuz sayıda olmak üzere- oyuncuların açık dünyada dolaşmasına, soygun veya görev gibi iş birliği gerektiren oyun modlarına katılmasına,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ölüm karşılaşması</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;veya yarış gibi rekabet gerektiren oyun modlarında rekabet etmesine olanak tanımaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyunun geliştirilme aşaması, Rockstar\'ın dünya genelindeki birçok stüdyosu tarafından&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto IV</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin piyasaya sürülme tarihinden kısa bir süre sonra başlatıldı. Geliştirme ekibi,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Red Dead Redemption</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Max Payne 3</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi önceki projelerden etkilendi ve temel yapının farklı olması için hikâyeyi üç ana karakterin etrafında tasarladı. Geliştirme çalışmalarının çoğu açık dünyanın yaratılışı üzerineydi. Bunun için birkaç ekip, Kaliforniya çevresinde saha araştırması yapmak için görevlendirildi. Oyun müzikleri Woody Jackson ile birlikte The Alchemist, Oh No ve Tangerine Dream tarafından bestelendi. Oyun, Eylül 2013\'te&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 3</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox 360</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için; Kasım 2014\'te&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 4</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için ve Nisan 2015\'te&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için yayımlandı.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 5</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox Series X/S</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;versiyonlarının Mart 2022\'de piyasaya sürülmesi planlanmaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun, ilk gününde 800 milyon dolar ve ilk üç gününde 1 milyar dolar hasılat elde ederek satış rekorları kırdı ve bu sayılara en kısa sürede ulaşan eğlence ürünü oldu.&nbsp;Karakter tasarımlarına, açık dünyasına, sunumuna ve oynanışına yönelik övgüler aldı ancak bir görevde işkence aletlerinin kullanılması, aşırı şiddet gösterimi ve sunduğu kadın tasviri nedeniyle birçok tartışmaya sebep oldu.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Target Corporation</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın Avustralya bölümü, oyunun \"oyuncuları cinsel şiddet uygulamaya ve kadınları öldürmeye teşvik ettiğini\" iddia eden bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Change.org</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kampanyası sonrası oyunu 300 mağazasından çıkardı.&nbsp;Karen Gravano ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Lindsay Lohan</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;da oyundaki bazı karakterlerin kendi görünüşlerine dayandığını iddia ederek Rockstar\'a karşı dava açtı.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Yedinci</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sekizinci</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;nesil konsol oyunlarının en önemli oyunlarından biri olarak kabul edilen ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Yılın Oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;da dâhil olmak üzere birçok ödül kazanan&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">GTA V,</span><span style=\"font-size: 14px; font-family: Helvetica;\"> 140 milyon kopya ile tüm zamanların en çok satılan ikinci video oyunudur ve dünya çapında yaklaşık 6 milyar dolar gelirle tüm zamanların finansal olarak en başarılı eğlence ürünlerinden biridir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Oynanış\" style=\"font-family: Helvetica; font-size: 24px;\"><b>Oynanış</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">GTA V,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">üçüncü şahıs</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;veya&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">birinci şahıs</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;açısıyla oynanabilen bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">aksiyon-macera oyunudur</span><span style=\"font-size: 14px; font-family: Helvetica;\">.&nbsp;Ana hikâye, birbirine bağlantılı altmış dokuz farklı görevden oluşmaktadır ve oyuncuların hikâye modunda ilerlemesi için bu görevleri tamamlaması gerekmektedir. Görevlerin dışında, oyuncular&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">açık dünyada</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serbestçe dolaşabilir. Modellenen açık dünya, önceden yayımlanmış olan tüm&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">GTA</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">oyunlarının sahip olduğu açık dünyalardan daha büyüktür.&nbsp;Büyük bir kısmının başlangıçta gizli olduğu açık dünya, hikâye ilerlemesiyle beraber kademeli olarak açılmaktadır. Bununla birlikte, gizlenmiş kısımların keşfedilmesi için hikâye ilerlemesi zorunlu değildir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyuncular, düşmanlarla savaşmak için yakın dövüş aletleri (beyzbol sopası, levye, pala, bıçak gibi), ateşli silahlar (tabanca, av tüfeği, keskin nişancı tüfeği gibi) veya patlayıcı silahlar (el bombası, bazuka, molotof kokteyli gibi) kullanabilir;&nbsp;açık dünyayı dolaşmak için koşabilir, zıplayabilir, yüzebilir veya araç kullanabilir.&nbsp;Oyunda, oyuncuların harita büyüklüğüne ayak uydurabilmesi için daha önce seride olmayan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sabit kanatlı hava araçları</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yer almaktadır. Çatışma sırasında, düşmanlara karşı avantaj sağlamak için otomatik nişan veya siper alma sistemi kullanılabilir.&nbsp;Oyuncular hasar aldıktan sonra sağlık göstergeleri yavaş bir şekilde orta noktaya kadar yenilenir.&nbsp;Eğer sağlıkları tamamen tükenirse en yakın hastanede yeniden doğarlar. Oyuncular, çevre tarafından fark edilmiş bir suç işlerse kolluk kuvvetleri tarafından aranmaya başlar.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Oyun arayüzünde</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;beliren yıldızlar, aranan seviyeyi gösterir.&nbsp;(Örneğin, beş yıldız seviyesindeki bir suçlu için olay yerine polis helikopterleri ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">SWAT</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ekipleri gelmektedir.)&nbsp;Eğer suçlu olay yerinden uzaklaşırsa arayüzde bulunan yıldızlar yanıp sönmeye başlar ve kolluk kuvvetleri saldırı pozisyonundan çıkıp suçluyu aramaya başlar. Eğer oyuncular, kolluk kuvvetlerinin mini haritada gözüken görüş alanından bir süreliğine daha gizlenirse; kolluk kuvvetleri geri çekilir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Tek oyunculu mod, oyuncuların üç ana karakteri kontrol etmelerine olanak tanımaktadır: Michael De Santa,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Trevor Philips</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve Franklin Clinton. Görevler tamamlandıkça üç suçlunun da hikâyesi birbiriyle bağlantılı olmaya başlar. Bazı görevler tek karakterle tamamlanabilirken bazılarında iki veya üç karakter yer almaktadır. Görevler dışında da -bu özellik bazı durumlarda kısıtlıdır- oyuncular ekranın altında beliren pusula ile üç karakter arasında geçiş yapabilir. Oyun, belirli hedefleri tamamlamak için görev sırasında otomatik olarak da karakter geçişi yaptırabilir. Karakterin pusuladaki avatarı eğer kırmızı renkte yanıp sönüyorsa bu tehlikede veya yardıma ihtiyacı olduğu; beyaz renkte yanıp sönüyorsa da o an stratejik bir avantajı olduğu anlamına gelmektedir. Oyuncular tüm görevleri üç ana karakter aracılığıyla bitirse de bazı soygun görevlerinde bilgisayar korsanlığı veya sürüş yeteneğine sahip suç ortakları gerekmektedir. Bu suç ortakları oyunun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yapay zekâsı</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından yönlendirilir. Eğer bir suç ortağı soygun görevini tamamlarsa kazanılan nakit paradan pay alır ve sonraki soygunların da bir parçası olabilir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Her karakterin, ateş etme veya araba kullanma gibi, belirli alanlardaki yeteneklerini temsil eden sekiz adet becerisi bulunmaktadır. Beceriler oyun boyunca geliştirilebilse de her karakter başlangıçta belirli bir uzmanlık alanına sahiptir. (Örneğin, Trevor\'ın uçuş becerisi) Her karakterin sekizinci \"özel\" becerisi ise birbirinden farklıdır: Michael çatışma sırasında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">mermi zamanına</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;girebilir, Franklin sürüş esnasında zamanı yavaşlatabilir, Trevor çatışmadayken normalde alacağı hasarın yarısını alabilir ve normalde vereceği hasarın iki katını düşmanlarına verebilir. Bu beceriler belirli bir süre için kullanılır ve mini haritada yer alan sarı çizgi tükendiğinde oyun akışı tekrardan normale döner.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyuncular, açık dünyada serbestçe dolaşırken aynı zamanda&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">BASE jumping</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;veya&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">aletli dalış</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi aktiviteler de yapabilir. Her karakter; arkadaşlarıyla iletişime geçmek, çeşitli etkinliklere başlamak ve oyun içi internete erişmek için bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">cep telefonuna</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;sahiptir. İnternet, oyuncuların borsa aracılığıyla&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">hisse senedi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;alıp satmasına olanak tanımaktadır.&nbsp;Oyuncular, garaj ve iş yeri gibi mülkler; çeşitli silahlar ve cephanelikler satın alabilir. Ek olarak&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sinema</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">striptiz kulübü</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi yerleri ziyaret edebilir. Kıyafet alımı, dövme yapımı ve saç kesimi sayesinde fiziksel görünümlerini değiştirebilir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Hik.C3.A2ye\" style=\"\"></span><span class=\"mw-headline\" id=\"Hikâye\" style=\"font-family: Helvetica; font-size: 24px;\">Hikâye</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-family: Helvetica; font-size: 14px;\">Oyunda&nbsp;Michael,&nbsp;Franklin&nbsp;ve&nbsp;Trevor&nbsp;isminde üç ana karakter mevcuttur. Oyundaki üç karakterden her birinin farklı alanlarda farklı yetenekleri ve güçleri vardır. Örneğin, Trevor eski bir savaş pilotu olduğu i<span style=\"font-family: Helvetica;\">çin&nbsp;</span></span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">uçak</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">jet</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">helikopter</span><span style=\"font-family: Helvetica; font-size: 14px;\"><span style=\"font-family: Helvetica;\">&nbsp;ve diğer hava araçlarını ku</span>llanma konusunda diğer karakterlerden daha iyidir. Michael ise eski bir banka soyguncusu olduğundan silah kullanma konusunda diğerlerinden daha iyidir. Franklin ise araç hırsızı olduğundan otomobil ve motosiklet sürme konusunda diğerlerine göre çok daha ustadır. Oyuncular karakterler arasında geçiş yapabilme imkanına sahiplerdir. Karakterler arasında geçiş yapılırken oyuncunun kullandığı karakterden<span style=\"font-family: Helvetica;\">&nbsp;</span></span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Google Earth</span><span style=\"font-family: Helvetica; font-size: 14px;\"><span style=\"font-family: Helvetica;\">&nbsp;benzeri b</span>ir haritaya doğru uzaklaşıp diğer karaktere yakınlaştırma yaparak geçiş yapılmaktadır. Karakterler arası mesafe ne kadar kısa ise geçiş işlemi de o kadar kısa olmaktadır.&nbsp; Oyunda jet ski sürme, uçuş yarışlarına (uçuş okuluna benzer ölçekte&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">uçak</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;yarışmaları) ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">off-road</span><span style=\"font-family: Helvetica; font-size: 14px;\"><span style=\"font-family: Helvetica;\">&nbsp;</span>yarışmalarına katılma,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">paraşütle</span><span style=\"font-family: Helvetica; font-size: 14px;\"><span style=\"font-family: Helvetica;\">&nbsp;</span>atlayış, akrobasi,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yoga</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">triatlon</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;yapma,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">tenis</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;oynama ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">golf</span><span style=\"font-family: Helvetica; font-size: 14px;\"><span style=\"font-family: Helvetica;\">&nbsp;oynama </span>gibi birçok küçük etkinlik mevcuttur. Her bir karakterin ayrı hobileri vardır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">GTA IV</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;ile birlikte seriye giren mobil telefon da bu seride biraz değiştirilmiş bir biçimde yer almaktadır. Mobil telefon daha çok&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px;\">internete</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;bağlanmak için kullanılmaktadır. iFruit isimli yeni telefondan resimler çekip Rockstar Social Club‘a yükleyebilme olanağı sunulmaktadır. <span style=\"font-family: Helvetica;\">Telefonda aynı zamanda&nbsp;</span></span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">kamera</span><span style=\"font-family: Helvetica; font-size: 14px;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">internet</span><span style=\"font-family: Helvetica; font-size: 14px;\">, kişiler,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sosyal medya</span><span style=\"font-family: Helvetica; font-size: 14px;\">, bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">takvim</span><span style=\"font-family: Helvetica; font-size: 14px;\"><span style=\"font-family: Helvetica;\">&nbsp;ve bitirilen bir görevi</span> tekrar yapabilme olanağı sunan bir uygulama yer almaktadır. Ve aynı zamanda i<span style=\"font-family: Helvetica;\">Fruit&nbsp;</span></span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">akıllı telefona</span><span style=\"font-family: Helvetica; font-size: 14px;\">&nbsp;indirilebilir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">GTA V\'de, önceki serilere göre daha çok araç bulunmaktadır.&nbsp;Önceki serilerdeki kontrol sistemi, hemen hemen bu oyunda da aynı şekildedir.&nbsp;Ayrıca,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Max Payne 3</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'teki&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">multiplayer</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;moduna benzer bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çoklu oyuncu modu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;vardır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyunun haritasının&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Red Dead Redemption</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto: San Andreas</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Grand Theft Auto IV</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunlarının haritalarından daha büyük olduğu açıklanmıştır. Oyundaki haritalardan birisi ise,çöl arazili olan Manhunt adlı oyundan bildiğimiz Carcer City olarak geçmektedir.&nbsp;Bazı özel korumalı bölgelerin dışında oyunun tüm haritası açıktır ve başlangıçtan itibaren tüm haritayı gezmek ve keşfetmek mümkündür. Oyundaki coğrafi alan büyük ve geniştir.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Dağlar</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">nehirler</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">göller</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">okyanuslar</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">askeri üsler</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">plajlar</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">tarım arazileri</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ormanlar</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çöller</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yoğun kentsel ortamlar</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;mevcuttur.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Okyanus</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tabanı ayrıntılı ve keşfedilebilmeyi bekler bir haldedir.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Dalış kıyafeti</span><span style=\"font-size: 14px; font-family: Helvetica;\">, oksijen tüpü ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">palet</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi dalış ekipmanlarıyla deniz altına dalabilmek mümkündür. Deniz altında doğal yaşam da mevcuttur. Bunun yanında deniz altında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">batıklara</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">hazinelere</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">zehirli mantarlara</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;rastlamak mümkündür.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">GTA San Andreas</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'taki istatistik özelliği bu oyunda da yer almaktadır. Her bir karakterin istatistik özellikleri bulunmaktadır. Dayanma gücü, dayanıklılık, hız, araba sürüşü, nişancılık ve “özel yetenekler” gibi istatistikler; sürüş, koşu, atış gibi aktiviteleri yaptıkça artış göstermektedir. Yine&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">GTA San Andreas</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyununda olduğu gibi bu oyunda da ev,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">garaj</span><span style=\"font-size: 14px; font-family: Helvetica;\">, işyeri satın almak veya kiralamak mümkündür. Taksi firması, tekne limanı,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">gece kulübü</span><span style=\"font-size: 14px; font-family: Helvetica;\">, golf sahası gibi işyerleri oyuncular tarafından satın alınabilmekte ya da kiralanabilmektedir. Önceki oyunlarda da olduğu gibi, karakterlerin saç, kıyafet ve dövmeleri özelleştirilebilmektedir. Yine otomobiller ve motosikletler Los Santos Customs adlı dükkânda modifiye edilebimektedir. Oyunda silah menüsü önceki oyunlardan çok farklı bir şekilde tasarlanmıştır.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Red Dead Redemption</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Max Payne 3</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunlarındakine benzer bir şekilde silahlar artık dairesel bir menüden seçilebilmektedir. Silahlar,</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">lazer</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;görüşü,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">dürbün</span><span style=\"font-size: 14px; font-family: Helvetica;\">,</span><span style=\"font-size: 14px; font-family: Helvetica;\"> susturucu, fener ve çeşitli yüksek mermi kapasitesine sahip şarjörlerle modifiye edilebilir. Oyunda aynı zamanda lipsync özelliği de bulunmaktadır.</span></p>', 1, 17, '/uploads/games/grand-theft-auto-v/grand-theft-auto-v-cover.jpg', '/uploads/games/grand-theft-auto-v/grand-theft-auto-v-1.jpg', '/uploads/games/grand-theft-auto-v/grand-theft-auto-v-2.jpg', '/uploads/games/grand-theft-auto-v/grand-theft-auto-v-3.jpg', '/uploads/games/grand-theft-auto-v/grand-theft-auto-v-4.jpg', '/uploads/games/grand-theft-auto-v/grand-theft-auto-v-5.jpg', '/uploads/games/grand-theft-auto-v/grand-theft-auto-v-6.jpg', '/uploads/games/grand-theft-auto-v/grand-theft-auto-v-7.jpg', 'https://www.youtube.com/embed/QkkoHAzjnUs', 'https://www.youtube.com/embed/N-xHcvug3WI', 'https://www.youtube.com/embed/olEGtoYs_8A', NULL, NULL, NULL, '2022-01-13 10:12:23', '2022-03-03 12:12:27');
INSERT INTO `games` (`id`, `name`, `category_id`, `developer_id`, `publisher_id`, `sys_req_min_id`, `sys_req_rec_id`, `game_details_id`, `slug`, `sub_title`, `description`, `status`, `hit`, `cover_image`, `image1`, `image2`, `image3`, `image4`, `image5`, `image6`, `image7`, `video1`, `video2`, `video3`, `video4`, `video5`, `deleted_at`, `created_at`, `updated_at`) VALUES
(15, 'PUBG: Battlegrounds', 2, 11, 8, 20, 20, 20, 'pubg-battlegrounds', 'PUBG: Battlegrounds ya da bilinen adıyla PUBG, Bluehole tarafından yayımlanan ve geliştirilmekte olan çok oyunculu bir video oyunudur.', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyunda 100\'e yakın (en fazla 100) oyuncu rotası belirlenmiş bir uçağın içinde oyuna başlarlar. İstedikleri zaman uçaktan atlarlar ve paraşütleriyle adanın herhangi bir konumuna inerler. Oyuncuların temel amacı çeşitli yerlerden buldukları çeşitli silahlarla diğer oyuncuları öldürüp ölmeden en sona kalan oyuncu olmaktır. Oyun esnasında adanın haritasındaki güvenli alan gittikçe küçülmeye başlar. Harita küçüldükçe oyuncular daha küçük bir alana yoğunlaşırlar ve birbirlerini görüp saf dışı bırakmaları ihtimali kolaylaşır. Oyunda en son hayatta kalan oyuncu/takım oyunu kazanır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun ilk olarak 23 Mart 2017 tarihinde&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Steam</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;erken erişim ile&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için çıkış yapmıştır. Çıkışından yaklaşık 3 ay sonra 4 milyondan fazla kopya sattı. Oyunun ilk ana sürümü&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Windows platformunda</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;20 Aralık 2017\'de erken erişimden çıkarak yayınlandı.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;versiyonu 12 Aralık 2017\'de piyasaya sürüldü. Mobil&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">battle-royale</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunlarının artmasıyla birlikte&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PUBG Mobile</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;9 Şubat 2018\'de&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Çin</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'de ilk çıkışını yaptıktan sonra 19 Mart 2018\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Android</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İOS</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;platformları için ücretsiz olarak yayınlandı.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Oynan.C4.B1.C5.9F\" style=\"\"></span><span class=\"mw-headline\" id=\"Oynanış\" style=\"font-size: 24px; font-family: Helvetica;\">Oynanış</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">PUBG, 100\'e yakın oyuncunun büyük ölçekli bir haritada birbirlerini öldürerek hayatta kalmaya çalıştıkları bir ölüm maçı oyunudur. Oyuncular oyuna tek başına, 2 ya da 4 kişilik takımlar hâlinde veya 4 kişilik takımlara karşı tek başına girebilir. Tek başına grup hâlinde girmeyi tercih eden oyuncular rastgele farklı oyuncularla takım olarak eşleştirilir. Her durumda oyuncular oyunu kazanmak için ölmeden en sona kalan oyuncu ya da takım olmaya çalışırlar. Takımdaki tüm oyuncular ölmeden takım dağılmaz.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Her maç oyuncular belirli bir rotada ilerleyen uçaklardan istedikleri zaman atlayarak paraşütle 8 x 8 kilometrelik geniş bir haritaya dağılırlar.&nbsp;Oyuncular haritaya indiklerinde oyuna bir etkisi olmayan kişisel kıyafet seçimleri dışında diğer oyunculara karşı üstünlük sağlayacak hiçbir şeyi olmaz. Oyuncular&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">kask</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">zırh</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çanta</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">silah</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">bomba</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">araç</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi çeşitli ekipmanlar bulmak için çeşitli binaları ve alanları ararlar. Zırh, kask, çanta gibi ekipmanlar 3 seviyeye sahiptir. Aynı zamanda silahlar için&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">dürbün (nişangâh)</span><span style=\"font-size: 14px; font-family: Helvetica;\">,</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">uzatılmış çekim şarjörü</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">namlu</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">susturucu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi geliştirmeler bulunabilir. Oyuncuların haritanın bazı belirli bölgelerinde daha iyi ekipman ve araçlar bulma ihtimalleri daha fazladır. Her oyuncu daha kaliteli ekipman bulmak istediğinden bu bölgelere daha çok akın ederler ve bu durum bu bölgeleri daha tehlikeli hâle getirir.&nbsp;Seviye arttıkça kapasiteleri ve kaliteleri de artar. Oyunda oyunculara 2 farklı görüş imkânı sunulur isteğe göre oyuncular&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">birinci şahıs</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;veya&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">üçüncü şahıs nişancı</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;modunda oynayabilirler. Oyun esnasında görüş perspektifini değiştirmek mümkündür.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Her birkaç dakikada bir harita rastgele bir bölgeye doğru küçülmeye başlar. Haritanın güvenli bölgesine gitmek için oyunculara belirli bir zaman verilir. Zamanında güvenli bölgeye varamayan oyuncular dışarıda kalan tehlikeli bölgeyi yavaşça saran mavi parlayan bir duvar ile karşılaşırlar.&nbsp;Oyuncular güvenli bölgeye ulaşabilmek için çeşitli sağlık paketlerini kullanırlar. Gittikçe daha çok hasar veren bu mavi parıltıdan kurtulamayan oyuncular ölürler.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun boyunca kırmızı bölge adı verilen haritanın rastgele belirlenen bir kısmına bomba yağar. Oyuncular bu bombalardan kurtulabilmek için yapıların içine saklanır veya o bölgeden uzaklaşmaya çalışır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bir tam oyun süresi ortalama 30 dakikadan fazla sürer. Her oyun sonu oyuncular hayatta kaldıkları süreye, öldürdükleri oyuncu sayısına, oyunculara verdiği hasara göre oyun içi para birimiyle ödüllendirilirler. Bu ödüllendirme sadece oyunu kazanan oyuncular için değil, tüm oyuncular için geçerlidir. Oyun içi para biriminin adı UC\'dir.</span><font face=\"Helvetica\"><span style=\"font-size: 14px;\">(Unknown Cash).&nbsp;Bu oyun içi paralar oyunculara ücretsiz kasa alma imkanı sunar. Kasaların fiyatı her satın alıştan sonra katlanarak artar ve anahtar gerektiren etkinlik kasaları dışında kasaları açmak ücretsizdir içinden rastgele bir eşya çıkar. Kasalar açılmadan veya açılırsa içerisinden çıkan eşyalar</span></font><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Steam</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;topluluk pazarında da satılabilir.</span></p>', 1, 10, '/uploads/games/pubg-battlegrounds/pubg-battlegrounds-cover.jpg', '/uploads/games/pubg-battlegrounds/pubg-battlegrounds-1.jpg', '/uploads/games/pubg-battlegrounds/pubg-battlegrounds-2.jpg', '/uploads/games/pubg-battlegrounds/pubg-battlegrounds-3.jpg', '/uploads/games/pubg-battlegrounds/pubg-battlegrounds-4.jpg', '/uploads/games/pubg-battlegrounds/pubg-battlegrounds-5.jpg', '/uploads/games/pubg-battlegrounds/pubg-battlegrounds-6.jpg', '/uploads/games/pubg-battlegrounds/pubg-battlegrounds-7.jpg', 'https://www.youtube.com/embed/URBy9t6e8rY', 'https://www.youtube.com/embed/c215QLferhs', 'https://www.youtube.com/embed/Zi2hMMVpS64', NULL, NULL, NULL, '2022-01-13 12:25:58', '2022-02-16 11:23:19'),
(16, 'Anno 1800', 3, 12, 9, 21, 21, 21, 'anno-1800', 'Anno 1800, Blue Byte tarafından geliştirilen ve Ubisoft tarafından yayınlanan ve 16 Nisan 2019\'da Kuzey Amerika\'da piyasaya sürülen bir şehir kurma gerçek zamanlı strateji video oyunudur.', '<p><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Anno</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisinin yedinci oyunudur ve 19. yüzyılda&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sanayi Devrimi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;sırasında geçen son iki oyun olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Anno 2070</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Anno 2205\'in</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ardından tarihi bir ortamın kullanımına geri döner. Önceki taksitin ardından oyun, serinin geleneksel şehir kurma ve okyanus savaşı mekaniğine geri dönüyor, ancak turizm,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">tasarım</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sanayileşme</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;etkilerinin ada sakinleri üzerindeki etkileri gibi oyunun yeni yönlerini sunuyor.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Genel_bakış\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Genel bakış</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">Oyunda ayrıca bir hikâye kampanyası, bir&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sanal alan modu</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çok oyunculu mod bulunuyor</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">.&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Anno 2205</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;gibi, oyun da çok oturumlu bir oynanışa sahip, ancak selefinin aksine, savaş ve şehir kurma oturumları birbirinden ayrılmamış.&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">Anno 1800</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">, klasik bir&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">şehir kurma oyununa</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;rastgele oluşturulmuş&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">haritalara</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ticaret yollarına</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">, oyuncuyla aynı harita üzerine inşa edilen&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yapay zeka (AI) rakiplerine</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;ve bir&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">orduya</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;entegre oluyor.&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">Anno 1800</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">, 19. yüzyılda&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sanayi Çağı\'nın</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;şafağında geçiyor.</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;Diğer Anno serisi oyunları gibi,&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">Anno 1800</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;de bir şehir kurma ve strateji oyunudur.&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sömürge ticareti</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;bağlamında kurulurken, öne çıkan mimari&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Viktorya Dönemi</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;ve ekonomik motor&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">fabrika işçiliğidir</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">.&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">Anno 1800</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">vatandaşların</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">, işçilerin ve&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">zanaatkârların</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;ihtiyaçlarının&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">üretim</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">tedarik zincirlerinin</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;yönetiminin merkezinde olduğu Eski Dünya\'da&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">geçiyor</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">. Eski Dünya\'daki&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">emekçilerin</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;satın almak istediği ürünleri üreten paralel bir Yeni Dünya şehri var, bu nedenle&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ticaret yollarının oluşturulması</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;gerekiyor. 18. yüzyıldan kalma sömürgeci selefi&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Anno 1701\'in</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">&nbsp;aksine, oyunda oyuncunun şehri planlamasına yardımcı olan bir plan özelliği&nbsp;</span><span style=\"text-align: var(--bs-body-text-align); font-size: 14px; font-family: Helvetica;\">var.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Anno 1800\'ün&nbsp;çekicilik derecesi,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sanayileşmenin</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">nüfus</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;üzerindeki etkisiyle boğuşuyor. Her fabrika ile şehrin çekicilik derecesi düşer ve gelişmekte olan bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sanayi bölgesi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;çevresindeki alanın derecesi de etkilenir.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Turistler</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şehrin gelirine katkıda bulunurlar ve doğal topraklara, yerel şenliklere ve çeşitli dekoratif süslemelere akın ederler, ancak&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">kirlilik</span><span style=\"font-size: 14px; font-family: Helvetica;\">, yerel huzursuzluk ve gürültülü veya kokulu endüstriden hoşlanmazlar.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Anno 1800\'ün&nbsp;taslak modu, seriye yeni eklenenlerden biridir.&nbsp;Oyuncuya, gerçekten onları inşa etmek için değerli kaynakları hemen harcamadan, kontrast planlı binalar ile şehirlerini planlamalarına izin vererek yardımcı olur. Bir oyuncunun kaynakları yetersizse, plan binası daha sonra inşa edilmek üzere şehir haritasına yerleştirilebilir. Planlama yapıldıktan ve gerekli kaynaklar toplandıktan sonra, her bir plan yapısı tek bir tıklama ile inşa edilebilir.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"DLC\" style=\"font-size: 24px; font-family: Helvetica;\"><b>DLC</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ücretli&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">indirilebilir içerik</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;(DLC) için&nbsp;Anno 1800&nbsp;sezon 1 geçişi, üç DLC paketi&nbsp;Sunken Treasure,&nbsp;Botanica&nbsp;ve&nbsp;The Passage\'ı içerir&nbsp;.&nbsp;Botanica&nbsp;şehirleri daha fazla turist çekmek için güzelleştirilebilirken,&nbsp;The Passage, oyuncunun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzey Kutup Dairesi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yolunda&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzeybatı Geçidi\'nde</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yelken</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;açmasına&nbsp;izin veriyor.&nbsp;2020\'de 2. sezon, üç DLC paketi daha içeriyor.&nbsp;Güç&nbsp;Makamı, oyuncunun bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">saray</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;çevresindeki&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yerleşimleri</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;hangi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">politikaların</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;etkileyeceğini seçmesine izin verir.&nbsp;Bright Harvest&nbsp;DLC\'si,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">traktörler</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">iş gücü azaltımı</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;getirecek.&nbsp;Land of Lions\'da&nbsp;oyuncunun bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çölü</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;keşfetmesi,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sosyal sınıfta</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gezinmesi ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">su kıtlığını gidermek</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sulama sistemi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kurması gerekecek.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Da.C4.9F.C4.B1t.C4.B1m\" style=\"\"></span><span class=\"mw-headline\" id=\"Dağıtım\" style=\"font-size: 24px; font-family: Helvetica;\">Dağıtım</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Anno 1800,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Epic Games Store</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Origin</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Uplay\'de mevcuttur</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Başlangıçta&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Steam\'de</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;önceden satın alınabilirdi, ancak resmi olarak piyasaya sürüldüğünde Steam\'den çekildi.</span></p>', 1, 52, '/uploads/games/anno-1800/anno-1800-cover.png', '/uploads/games/anno-1800/anno-1800-1.jpg', '/uploads/games/anno-1800/anno-1800-2.jpg', '/uploads/games/anno-1800/anno-1800-3.jpeg', '/uploads/games/anno-1800/anno-1800-4.jpg', '/uploads/games/anno-1800/anno-1800-5.jpg', '/uploads/games/anno-1800/anno-1800-6.jpeg', '/uploads/games/anno-1800/anno-1800-7.jpg', 'https://www.youtube.com/embed/UowsqoV0egc', 'https://www.youtube.com/embed/VO_MLefLW-Q', NULL, NULL, NULL, NULL, '2022-01-13 14:55:47', '2022-02-16 11:10:35'),
(17, 'Humankind', 3, 13, 10, 22, 22, 22, 'humankind', 'Humankind, Amplitude Studios tarafından geliştirilen ve Sega tarafından yayınlanmış yeni bir 4X video oyunudur.', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Humankind,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Civilization</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisine benzer bir 4X oyunudur. Oyuncular, göçebe dönemden başlayarak,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">medeniyetlerini</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;altı büyük insan uygarlığı çağında yönetirler. Oyuncular, yeni bir oyunun başlangıcında rastgele oluşturulmuş sanal gezegendeki diğer medeniyetlerle etkileşime girerken medeniyetlerini genişletmeli, şehirleri geliştirmeli, orduları ve diğer birimleri kontrol etmelidir. Humankind\'ın ayırt edici bir özelliği, her bir çağda, oyuncunun tarihsel toplumlara dayanan on medeniyet türünden birini seçmesidir; bu seçim, oyuncunun medeniyeti nasıl inşa edebileceğine hem bonuslar hem de cezalar sunar. Bir oyuncu, üzerine inşa edilecek bir şablon olarak farklı medeniyetler seçebildiğinden, oyun sonunda geliştirebileceği potansiyel olarak bir milyon farklı medeniyet modeli vardır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Şehirleri inşa etmek, Amplitude\'un&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Endless Legend</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunundaki benzer bir modeli takip eder, Humankind\'daki bir kıta, üzerinde birden fazla bölgeye sahip olacak ve oyuncu, o bölgede yalnızca bir şehir inşa edebilecek. Zamanla, bu şehri genişletebilir, çiftlikleri ve diğer uzak kaynakları ve şehir merkezine daha yakın olan daha yoğun kentsel alanları ekleyebilirler. Bu, her bölgede büyük metropollerin oluşturulmasına izin verir. Oyuncuların ayrıca düşman kuvvetleriyle savaşması gerekebilir. Bu gerçekleştiğinde, oyun bu savaşların ayrıntılı çözümü için&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">taktiksel bir rol yapma oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yaklaşımı kullanır ve oyuncuya araziden ve birimlerinin özel yeteneklerinden yararlanma şansı verir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Geli.C5.9Ftirme\" style=\"\"></span><span class=\"mw-headline\" id=\"Geliştirme\" style=\"font-size: 24px; font-family: Helvetica;\">Geliştirme</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Humankind,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">gamescom</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;2019\'da duyuruldu.&nbsp;Oyun, 2016 yılında yayıncı Sega tarafından satın alınan Amplitude Studios tarafından geliştirilmiştir.&nbsp;Stüdyo, 2012 ve 2017 yıllarında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Endless Space</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Endless Space 2</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;de dahil olmak üzere&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">4X</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">sıra tabanlı strateji</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunları yayınladı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Haziran 2020\'deki&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PC Gaming Show</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da, oyunun nihayet 2021\'de piyasaya sürüleceği duyuruldu. Geliştirici ayrıca oyuncuları oyunun geliştirilmesine farklı şekillerde katılmaya davet ettiğini, örneğin beklentileri hakkında geri bildirim sunabileceklerini açıkladı.&nbsp;21 Ekim 2020\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Stadia</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">demo</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;başlatıldı.</span></p>', 1, 5, '/uploads/games/humankind/humankind-cover.jpg', '/uploads/games/humankind/humankind-1.jpg', '/uploads/games/humankind/humankind-2.jpg', '/uploads/games/humankind/humankind-3.jpg', '/uploads/games/humankind/humankind-4.jpg', '/uploads/games/humankind/humankind-5.jpg', '/uploads/games/humankind/humankind-6.jpg', '/uploads/games/humankind/humankind-7.jpg', 'https://www.youtube.com/embed/nGi00HqpmS0', 'https://www.youtube.com/embed/-QlxCQThAI8', 'https://www.youtube.com/embed/Pw-yKOte5eA', NULL, NULL, NULL, '2022-02-16 10:44:28', '2022-02-16 11:21:04'),
(18, 'Death Stranding', 1, 14, 6, 23, 23, 23, 'death-stranding', 'Death Stranding, Kojima Productions tarafından geliştirilen ve Sony Interactive Entertainment tarafından yayınlanan video oyunudur.', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sözleşmesinin sona ermesinin ardından 2015 yılında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Konami</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'den ayrılan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hideo Kojima</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve şirketinin yapmış olduğu ilk oyundur. Amerikalı aktör&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Norman Reedus</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyundaki ana karakteri canlandırıp seslendirmiştir. Oyun ilk defa Haziran 2016\'da&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sony</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin düzenlediği&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">E3 2016</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;konferansında duyuruldu. Eylül 2016\'da Kojima, oyunun 2019 yılından önce yayınlanacağını belirtti.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hideo Kojima</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunun aksiyon türüne benzer bir türde olduğunu fakat yeni ve değişik elementleri içerdiğini belirtti.&nbsp;Oyunun ana temalarından bazılarının&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kōbō Abe</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'in kısa hikâyelerinden esinlenerek oluştuğunu açıkladı.&nbsp;Bu oyun Kojima ve aktör Norman Reedus\'ın ikinci çalışmasıdır, ilki 2015\'te iptal edilen&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Silent Hills</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'dir. Kojima\'ya göre oyun fragmanının kilit unsurlarından biri yaşam ve ölümün bağlantısı fikridir.&nbsp;Fragmanda ayrıca kıyıya vurmuş ölü canlılar teması ve görselleri hakimdir.&nbsp;1 Aralık 2016 tarihinde düzenlenen&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">2016 The Game Ödülleri</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nde yayınlanan fragmanda&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Mads Mikkelsen</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'in oyunda yer aldığı, daha önceden Kojima ve Reedus ile birlikte&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Silent Hills</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;projesinde yer almış film yönetmeni&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Guillermo del Toro</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun da oyunda küçük bir role sahip olduğu açıklandı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyunun PC versiyonunun olma olasılığı konusu ilk kez Aralık 2015\'te&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Medium</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'un yayınladığı Soru&amp;Cevap oturumunda bahsedildi&nbsp;ve Kasım 2019\'da Windows\'ta yayınlanacağı kesinleşti.</span></p>', 1, 13, '/uploads/games/death-stranding/death-stranding-cover.jpg', '/uploads/games/death-stranding/death-stranding-1.jpg', '/uploads/games/death-stranding/death-stranding-2.jpg', '/uploads/games/death-stranding/death-stranding-3.jpg', '/uploads/games/death-stranding/death-stranding-4.jpg', '/uploads/games/death-stranding/death-stranding-5.jpg', '/uploads/games/death-stranding/death-stranding-6.jpg', '/uploads/games/death-stranding/death-stranding-7.jpg', 'https://www.youtube.com/embed/tCI396HyhbQ', 'https://www.youtube.com/embed/UcaMw4aCz4c', 'https://www.youtube.com/embed/5pG6HrnVOJE', NULL, NULL, NULL, '2022-02-16 13:50:11', '2022-03-03 12:12:46');
INSERT INTO `games` (`id`, `name`, `category_id`, `developer_id`, `publisher_id`, `sys_req_min_id`, `sys_req_rec_id`, `game_details_id`, `slug`, `sub_title`, `description`, `status`, `hit`, `cover_image`, `image1`, `image2`, `image3`, `image4`, `image5`, `image6`, `image7`, `video1`, `video2`, `video3`, `video4`, `video5`, `deleted_at`, `created_at`, `updated_at`) VALUES
(19, 'Elden Ring', 1, 15, 11, 24, 24, 24, 'elden-ring', 'Elden Ring FromSoftware tarafından geliştirilmekte olan ve Bandai Namco Entertainment tarafından yayınlanacak olan yeni bir aksiyon-rol yapma oyunudur.', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Elden Ring, E3 2019 fuarında duyurulan ve geliştirmesi hala devam etmekte olan bir aksiyon-rol yapma oyunudur. Oyun,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">FromSoftware</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından geliştirilmektedir ve yayımcılığını&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bandai Namco Entertainment</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;üstlenmektedir;&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 4</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;platformlarında çıkış yapacaktır.&nbsp;Oyunun yönetmenliğini&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Souls</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisi,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bloodborne</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sekiro: Shadows Die Twice</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi oyunlar ile tanınan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hidetaka Miyazaki</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;üstlenmektedir, oyunun dünyasını ise özellikle&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Buz ve Ateşin Şarkısı</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kitabı ile tanınan fantezi yazarı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">George R. R. Martin</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yaratmıştır.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">George R. R. Martin</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'in çalışmalarının büyük bir hayranı olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hidetaka Miyazaki</span><span style=\"font-size: 14px; font-family: Helvetica;\">, Martin ile iletişime geçmiş ve oyunun dünyasını oluşturması için kendisine kreatif özgürlük tanımıştır. Miyazaki,&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">IGN&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">için verdiği bir röportajda, oyunun isminin anlamının sorulması üzerine \"</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Yüzüklerin Efendisi</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;en klasik fantezi romanı, bundan dolayı&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">Elden Ring</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;bu romandan belli bir miktarda esinlenilerek ama tamamen aynısı olmayacak şekilde yapıldı.\" dedi ve Elden Ring ismindeki \"Ring\" kelimesinin aslında parmağa takılan bir yüzük anlamına gelmediğini, \"yuvarlak\" anlamına geldiğini belirtti.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Elden Ring\'in geliştirilmesine 2017\'nin başlarında,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Dark Souls III</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için geliştirilen&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">The Ringed City</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">indirilebilir içeriğinin</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yayımlanmasının hemen ardından başlandı. Diğer&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Souls</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunlarında olduğu gibi,&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">Elden Ring</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;sabit bir ana karakter ile oynamak yerine oyunculara kendi ana karakterlerini yaratabilme özelliğini tanıyacak.&nbsp;Oyun, her ne kadar ana mantığını&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Souls</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisinden alacak olsa da, diğer&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Souls</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunlarının aksine,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">oyuncu olmayan karakterlerin</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;(</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Npc</span><span style=\"font-size: 14px; font-family: Helvetica;\">) çok fazla bulunmadığı, zindan benzeri harabeler içeren daha büyük bir açık dünya, at sürme ve at üstünde savaş gibi yeni özelliklere sahip olacak.&nbsp;Miyazaki\'ye hikâyenin kitaplaştırılma ihtimali sorulduğunda kendisi, oyuncuların oyunu oynayarak hikâyeyi öğrenmelerini ve deneyim etmelerini tercih ettiğini söylemiştir.&nbsp;Oyunun müzikleri, daha önce de Miyazaki tarafından geliştirilen oyunların birçoğunda bestecilik yapan Yuka Kitamura tarafından bestelenmektedir.</span></p>', 1, 3, '/uploads/games/elden-ring/elden-ring-cover.webp', '/uploads/games/elden-ring/elden-ring-1.jpg', '/uploads/games/elden-ring/elden-ring-2.jpg', '/uploads/games/elden-ring/elden-ring-3.jpg', '/uploads/games/elden-ring/elden-ring-4.jpg', '/uploads/games/elden-ring/elden-ring-5.jpg', '/uploads/games/elden-ring/elden-ring-6.jpg', '/uploads/games/elden-ring/elden-ring-7.jpg', 'https://www.youtube.com/embed/AKXiKBnzpBQ', 'https://www.youtube.com/embed/JldMvQMO_5U', NULL, NULL, NULL, NULL, '2022-03-03 12:42:08', '2022-03-03 12:46:00'),
(20, 'The Sims 4', 3, 16, 2, 25, 25, 25, 'the-sims-4', 'The Sims 4, Maxis ve The Sims Studio tarafından geliştirilen ve Electronic Arts tarafından yayınlanan 2014 tarihli yaşam simülasyonu oyunudur.', '<p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">The Sims</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisinin dördüncü oyunudur. Electronic Arts, 6 Mayıs 2013 tarihinde bir duyuru yaparak oyunu 2014 yılında piyasaya süreceğini ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Mac OS X</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;platformlarında oynanacağını bildirdi.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;platformu için&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzey Amerika</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da 2 Eylül 2014 tarihinde satışa sunuldu. Mac sürümü içinse 17 Şubat 2015 tarihinde dijital platformdan indirilmeye açık hale geldi. Oyunun konsol sürümü 11 Kasım 2017\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 4</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için çıktı. Serinin önceki oyunlarından farklı olarak bilgisayar ve konsol sürümü arasında farklar bulunmaz. Oyun piyasaya çıktığından beri olumlu ve olumsuz birçok eleştiri aldı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Önceki oyunlarla aynı konsepte sahiptir ve oyuncular Simleri fiziksel aktivitelerle kontrol edip başkalarıyla iletişim kurdurabilirler. Oyun önceki oyunlar gibi bir kurguya bağlı olmadan ilerler. Oyunun bu sürümünde sim yaratma ve inşa modu ekranları tekrar dizayn edilmiştir. Serinin önceki oyunlarına göre; bu oyunda duygusal bağlar, simlerin kişiliği, sosyal davranışlar daha ön plandadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyun için on adet genişleme paketi yayınlandı. Bunlardan en sonuncusu&nbsp;Snowy Escape&nbsp;adıyla 13 Kasım 2020\'de piyasaya sürüştü. Ayrıca on sekiz adet \"eşya paketi\" ve dokuz adet \"oyun paketi\" adı altında içerikler yayınlandı ve oyunun bu jenerasyonuna düzenli olarak gelen güncellemeler ile birçok büyük değişiklikler yapıldı.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Oynanış\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Oynanış</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">The Sims 4, serinin önceki oyunları gibi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yaşam simülasyonu oyunudur</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Oyuncular bir Sim karakteri yaratırlar ve oyunda farklı Sim kişiliklerini keşfederek oyuna yön verirler. Simler daha önceki oyunlardan farklı olarak aynı anda iki fiziksel eylemi gerçekleştirebilir. Ayrıca Simlerin modları da oyun akışını değiştirebilir. Örneğin kızgın ya da heyecanlı bir Sim, verilen bir görevi daha hızlı yapar.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Sim_yaratma\" style=\"font-size: 20px; font-family: Helvetica;\"><b>Sim Yaratma</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Eski Sims oyunlarına göre, Sim yaratma ekranındaki en büyük değişiklik, fare ile Sim\'in tüm vücudunu oyuncunun istediği oranlara göre ayarlanmasıdır. Fareyi çekip bırakarak Sim\'in herhangi bir bölgesinde genişleme ya da daraltma yöntemleri uygulanabilir. Özellikle yüz bölgesindeki ayarlamalar oyunu önceki seri oyunlarından ayıran en büyük farklılıktır. Simlerin vücut ve kilo yapısı ayarları önceki oyunlar gibidir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ana oyun -eklenti ve paketler hariç- erkek ve kadın Simler için kırkın üzerinde saç stili sunar, farklı etnik kökenleri ve farklı vücut şekilleri olan hazır Simler mevcuttur, Sim yaratırken farklı cinsel yönelim ve cinsiyet akışkanlığına uyum sağlar, sadece feminen kadın ya da maskülen erkek yaratmak zorunda değilsiniz.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Altı farklı yaş evresi vardır: bebek, küçük çocuk, çocuk, ergen, genç yetişkin, yetişkin ve yaşlı. Sim yaratma ekranında bebek oluşturmak erişime kapalı olsa da seçili iki Sim\'in</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">DNA</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ları</span><span style=\"font-size: 14px; font-family: Helvetica;\"> birleştirilerek rastgele olarak, oyun size bebek yaratır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Her Sim üç adet kişisel özellik seçer ve bununla beraber oyun otomatik olarak bir hayat arzusu sunar.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\"Sat.C4.B1n_alma_ve_in.C5.9Fa_modu\" style=\"\"></span><span class=\"mw-headline\" id=\"Satın_alma_ve_inşa_modu\" style=\"font-size: 20px; font-family: Helvetica;\">Satın Alma ve İnşa Modu</span></b></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">The Sims 4\'te satın alma ve inşa modu birleştirilerek aynı seçenek altında kombinlenmiş halde ekrana gelir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Satın alma ve inşa modunda peyzaj düzenleme eşyaları mevcuttur. Bazı kilitli ögeler, kariyer ilerleme seviyelerine göre kullanıma açılır. Arsadaki yapı ya da ayrı odalar, bütün halde taşınabilme özelliğine sahiptir. Duvar yükseklikleri üç farklı haldedir. Bu modda ayrıca eşyanın ya da aracın daha kolay bulunabilmesi için arama özelliği eklenmiştir.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Galeri\" style=\"font-size: 20px; font-family: Helvetica;\"><b>Galeri</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">The Sims 4\'te sosyal bağlantılara önem verilip oyuncuların internet aracılığıyla çevrimiçi halde galeriye ulaşması ve buradan oyuncuların galeri sistemine yüklediği çeşitli hazır Simler, evler ve yapılar indirebilmesine olanak sunulmuştur. 9 Ocak 2015 tarihinde Galeri,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">iOS</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Android</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;cihazlarına geldi.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><span class=\"mw-headline\" id=\"Kasabalar\" style=\"font-size: 20px; font-family: Helvetica;\"><b>Kasabalar</b></span></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">The Sims 4 ana oyunuyla Willow Creek ve Oasis Springs adlı iki kasabayla gelir. İki kasabada da ayrı ayrı beş hane bulunur. Ayrıca ücretsiz güncellemeyle Newcrest adlı boş kasaba da oyuna eklenir.&nbsp;The Sims 4: Get to Work ile oyuna Magnolia Promenade adında 4 arsalı ufak bir dünya eklenir. The Sims 4: Get Together ile Windenburg kasabası oyuna entegre edildi.</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">City Living</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;adlı üçüncü genişleme paketi ile San Myshuno adındaki kasaba oyuna eklendi. Cats and Dogs genişleme paketi ile birlikte oyuna Brindleton Bay adında yeni bir balıkçı kasabası eklendi. Get Famous eklenti paketi ile birlikte Del Sol Valley adında bir kasaba daha oyuna entegre edildi. Island Living eklentisiyle birlikte oyuna Sulani adında yeni bir deniz kasabası eklendi. Discover University ile birlikte Britechester adında bir üniversite kasabası eklendi. Son çıkan eklenti paketi Eco Lifestyle ile birlikte kirlilik düzeyi farklı mahalleler bulunan Evergreen Harbor adında bir liman kenti eklendi.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">The Sims 4\'e kasaba getirmeyen tek eklenti paketi Seasons eklenti paketidir. Yeni bir dünya getirmek yerine var olan dünyalara hava durumu getirir. Oasis Springs, Del Sol Valley, Sulani ve Selvadorada\'da kışın kar yağmaz fakat Sulani\'de diğer dünyalarda bulunmayan \"muson rüzgarları\" adında yeni bir hava durumu vardır.&nbsp;18 Haziran 2020 tarihinde&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Wayback Machine</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;sitesinde&nbsp;</span><span style=\"background-image: url(&quot;/w/skins/Vector/resources/common/images/external-link-ltr-icon.svg?48e54&quot;); background-position: right center; background-size: initial; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; padding-right: 13px; font-size: 14px; font-family: Helvetica;\">arşivlendi.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Geli.C5.9Ftirme\" style=\"\"></span><span class=\"mw-headline\" id=\"Geliştirme\" style=\"font-size: 24px; font-family: Helvetica;\">Geliştirme</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">The Sims 4, The Sims Studio tarafından geliştirildi,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Electronic Arts</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve yan kuruluşu olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Maxis</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından piyasaya dağıtıldı. Geliştirilmesi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">The Sims 3</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile aynı şekilde oldu fakat&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Maxis</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'in desteği The Sims 4 için daha fazlaydı.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">The Sims 2</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunundan beri geliştirilmeye başlanmasından itibaren Maxis desteği alan ilk oyundur. Oyun geliştirilmeye başlandığı anda The Sims 3 için destek tamamen bitmiştir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">The Sims 4, tek oyunculu oynanan bir oyundur&nbsp;ve oynanması için internet bağlantısına ihtiyaç yoktur. Orijinal oyun yüklenirken aktif edilmesi için&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Origin</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'e internet aracılığıyla bağlanılıp aktif edilmesi gerekir.&nbsp;Oyunun soundtrack müzikleri için&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Abbey Road Studios</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Ilan Eshkeri</span><span style=\"font-size: 14px; font-family: Helvetica;\">, tüm müzik geliştirmesini ve kayıtlarını London Metropolitan Orchestra ile yapmıştır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">25 Nisan 2013 tarihinde internete The Sims 4\'ün geliştirilmekte olan motoruna ait The Sims 4: Olympos demo taslak resimleri düştü. 3 Mayıs 2013 tarihinde&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Electronic Arts</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">The Sims</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;fan sitelerine e-posta atarak serinin dördüncü oyununu&nbsp;The Sims 4&nbsp;olarak piyasaya sürüleceğini duyurdu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">TheSims4.com domain ismi aynı tarihte aktif edildi ve resmi sitesi olduğu onaylandı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">20 Ağustos 2013 tarihinde&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Gamescom</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;etkinliğinde The Sims 4 oynanış videosu gösterildi ve oyun resmen oyunculara tanıtıldı.&nbsp;Bununla birlikte oyunun demo sürümlerinden resimler 2014 yılına kadar sık sık yayınlandı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Oyunun çıkış tarihi için her ne kadar 2014 ilk çeyreği dense de oyun Electronic Arts tarafından 2 Eylül 2014\'te yayınlanacağı açıklandı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">14 Mayıs 2014 tarihinde yapımcı Ryan Vaughan, resmi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Youtube</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kanalında Sim yaratma ekranından bir fragman yayınladı. Bu fragmanda oyunun önceki serilerinde hikâyeleri mevcut olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bella Goth</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Mortimer Goth</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'un oluşturulma aşamaları mevcuttu. 28 Mayıs 2014 tarihinde ise Satın alma ve inşa modunun final haliyle tanıtıcı bir fragman daha yayınlandı. Bu fragman; duvarların üç farklı boyutunu, pencerelerin duvardaki hizasını ve herhangi bir odanın, evin istediği bir tarafına tek bir hamlede taşınacağını içeriyordu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">The Sims 4; 2 Eylül 2014\'te&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzey Amerika</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da, 4 Eylül 2014\'te ise&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avrupa</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avustralya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Brezilya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da piyasaya çıktı.</span></p>', 1, 3, '/uploads/games/the-sims-4/the-sims-4-cover.jpg', '/uploads/games/the-sims-4/the-sims-4-1.jpg', '/uploads/games/the-sims-4/the-sims-4-2.jpg', '/uploads/games/the-sims-4/the-sims-4-3.jpg', '/uploads/games/the-sims-4/the-sims-4-4.jpg', '/uploads/games/the-sims-4/the-sims-4-5.jpg', '/uploads/games/the-sims-4/the-sims-4-6.jpg', '/uploads/games/the-sims-4/the-sims-4-7.jpg', 'https://www.youtube.com/embed/z00mK3Pxc8w', 'https://www.youtube.com/embed/WjPPjU8OARg', NULL, NULL, NULL, NULL, '2022-03-03 13:19:42', '2022-03-03 13:21:22');

