  `description` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `games_count` bigint(20) DEFAULT NULL,
  `status` tinyint(4) NOT NULL DEFAULT 1 COMMENT '0:pasif 1:aktif',
