INSERT INTO `publishers` (`id`, `name`, `slug`, `description`, `games_count`, `status`, `image`, `deleted_at`, `created_at`, `updated_at`) VALUES
(1, 'Rockstar Games', 'rockstar-games', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Rockstar Games, diğer bir adı&nbsp;Rockstar NYC&nbsp;olan, Take-Two-Interactive\'e bağlı uluslararası video oyun yapımcı firması. Firma ABD New York\'ta kurulmuştur. Başta&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">G</span><span style=\"font-size: 14px; font-family: Helvetica;\">rand Theft Auto&nbsp;olmak üzere&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Max Payne</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">L.A. Noire</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">The Warriors</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bully</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Manhunt</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Midnight Club</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">State of Emergency</span><span style=\"font-size: 14px; font-family: Helvetica;\">, ve</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">&nbsp;Red Dead&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">gibi açık dünya ve serbest dolaşımlı oynanışı temel alan oyunlarıyla tanınmıştır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Rockstar Games 1998\'de Take-Two Interactive\'in daha önce BMG Interactive\'den satın aldığı varlıkları kullanarak Take-Two Interactive\'in bir yan kuruluşu olarak Aralık 1998\'de Sam Houser, Terry Donovan, Dan Houser, Jamie King ve Gary Foreman tarafından kuruldu.</span></p>', 2, 1, '/uploads/publishers/rockstar-games/rockstar-games.png', NULL, '2021-12-28 06:43:12', '2022-01-13 12:02:21'),
(2, 'Electronic Arts', 'electronic-arts', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Electronic Arts Inc.&nbsp;(EA), merkezi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Redwood</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kaliforniya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da bulunan Amerikalı bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyun</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketi. Mayıs 2020 itibarıyla Amerika ve Avrupa\'da kâr ve piyasa değeri bakımından ikinci en büyük oyun şirketidir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Hisseleri&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">NASDAQ</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da&nbsp;EA&nbsp;ismiyle işlem görmektedir. Dünya çapında pek çok stüdyosu vardır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\"><br></span><span style=\"text-align: var(--bs-body-text-align); font-size: 18px; font-family: Helvetica;\"><b>Tarihçe</b></span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">EA, 1982 yılında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Trip Hawkins</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından kurulmuştur. Bu alanda açılan ilk şirketlerden biridir. İlk yılllarında daha çok bilgisayar satışına ağırlık vermiş, bu arada sadece bazı oyunların yayımcılığını üstlenmiştir. 80\'lerin sonlarına doğru&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">oyun konsolları</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için de çalışmaya başlamıştır. 90\'lı yılların sonları ve 2000\'li yıllarda&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Medal of Honor</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Need for Speed</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Battlefield</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serileri sayesinde ünlü bir oyun şirketi haline gelmiştir. Günümüzde birçok oyunun yapımcılığını yapmaktadır.</span></p>', 1, 1, '/uploads/publishers/electronic-arts/electronic-arts.png', NULL, '2022-01-03 14:14:45', '2022-01-04 13:16:44'),
(3, 'Deep Silver', 'deep-silver', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Deep Silver,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Planegg</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli Koch Media\'nın&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yayıncılığı için kurulmuş bir bölümüdür.&nbsp;Deep Silver şu üç oyun geliştirme stüdyosuna sahiptir: Champaign,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Saints Row</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisinin geliştiricisi&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Illinois</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli Volition;&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Homefront: The Revolution</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'u geliştiren Nottingham,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İngiltere</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli Dambuster Studios ve en bilineni Galaxy on Fire serisi olmak üzere&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">mobil oyunlar</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;geliştiren&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hamburg</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Almanya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"font-size: 14px; font-family: Helvetica;\">merkezli Fishlabs.&nbsp;Eugenio Vitale Deep Silver\'ın oyun geliştirme müdürüdür.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Tarihçe\" style=\"font-size: 18px; font-family: Helvetica;\"><b>Tarihçe</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Deep Silver, ilk yayınlanan oyunları Anarch Online: The Notum Wars ile birlikte Kasım 2002\'de duyuruldu.&nbsp;Koch Media\'nın İngiliz kolunu yöneten kişi olan Craig McNichol\'e göre Deep Silver\'ın arkasındaki amaç Koch Media\'nın diğer yayıncılar adına dağıttığı oyunlara tamamlayıcı olacak video oyunları üzetecek bir sektör bölümüne sahip olmaktı.&nbsp;McNichol ayrıca Deep Silver\'ın isminin içeride birçok tartışmaya yol açtığını belirtti. Koch Media Temmuz 2003\'te Deep Silver\'a 500 bin euro yatırım yaptı ve Kasım 2003\'te Koch Media\'nın dağıtımcılık hariç tüm yayıncılık faaliyetleri Deep Silver\'a tahsis edildi.&nbsp;Bölüm o zamandan beri birincil olarak Avrupa\'da olmak üzere devamlı aktifti.&nbsp;Nisan 2008\'de Koch Media Deep Silver\'ın adına&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Los Angeles</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli Deep Silver Inc. adında bir alt şirket açtı.&nbsp;Ağustos 2007\'de eski Rockstar Vienna çalışanlarının kurduğu Games That Matter, Koch Media tarafından satın alındı ve Deep Silver Vienna adında Deep Silver\'ın bir parçası haline geldi.</span><a href=\"https://tr.wikipedia.org/wiki/Deep_Silver#cite_note-10\" style=\"line-height: 1; unicode-bidi: isolate; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 14px; font-family: Helvetica;\">]</span></a><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;Ortak kurucular Niki Laber ve Hannes Seifert Ocak 2010\'da stüdyodan ayrıldı ve bir noktada Deep Silver Vienna kapatıldı.&nbsp;Deep Silver Vienna\'nın ürettiği tek oyun Ağustos 2009\'da&nbsp;</span><span style=\"line-height: 1; unicode-bidi: isolate; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Wii</span><span style=\"line-height: 1; unicode-bidi: isolate; font-size: 14px; font-family: Helvetica;\">&nbsp;için Sproing Interactive ile birlikte üretilen Cursed Mountain\'di.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Deep Silver ilk olarak&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Dead Island</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın çıkışında ve Volition\'un satın alımında geniş çapta ilgi çekti.&nbsp;Eylül 2011\'de&nbsp;Dead Island&nbsp;en iyi satanlar listesinde en üst sırada yer alan ilk oyunlarıydı ve Ocak 2013\'te Volition\'la birlikte&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">THQ</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun iflas davalarından Metro serisinin haklarını elde ettiler.&nbsp;Deep Silver ayrıca Aralık 2012\'de Berlin merkezli oynaması ücretsiz oyun geliştiricisi Infernum Productions\'ta azınlık hissesi elde etti.&nbsp;Şubat 2013\'te Deep Silver mobil oyun pazarına girme planlarının olduğunu açıkladı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Aralık 2013\'te Koch Media Fishlabs\'ı satın aldı ve Fishlabs Deep Silver\'ın özel mobil oyun stüdyosu haline geldi.&nbsp;Anlaşmanın bir varlık anlaşması olduğundan dolayı, stüdyonun yasal mevcudiyeti feshedildi ve Fishlabs resmi olarak Deep Silver Fishlabs olarak tekrar düzenlendi.&nbsp;Temmuz 2014\'te Deep Silver Homefront\'un ve geliştirilmekte olan devam oyunu Homefront: The Revolution\'u Alman geliştirici&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Crytek</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ten satın aldı.&nbsp;Dambuster Studios (resmi olarak, Deep Silver Dambuster Studios)&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Crytek UK</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'in bıraktığı yerden Homefront: The Revolution\'un geliştirilmesine devam etmesi için kuruldu. Aynı gün Crytek, Crytek UK\'in kapatılacağını ve tüm çalışanlarının Dambuster Studios\'a transfer edileceğini duyurdu.&nbsp;Ağustos 2018\'de Koch Media Deep Silver tarafından kontrol edilecek TimeSplitters oyunlarının haklarını satın aldı.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Ele.C5.9Ftiriler\" style=\"\"></span><span class=\"mw-headline\" id=\"Eleştiriler\" style=\"font-size: 18px; font-family: Helvetica;\">Eleştiriler</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ocak 2013\'te Deep Silver, o zamanki çıkacak oyunları Dead Island: Riptide için&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avrupa</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avustralya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da Zombie Bait Edition adı altında parçalanmış bir kadın gövdesinin heykelini içeren özel bir sürümünü duyurdu.&nbsp;Eşya üzerinde yapılan ağır eleştirilerden sonra Deep Silver başlangıçta çok üzgün olduklarını belirten ve müşterilerine bir daha böyle bir şeyin gerçekleşmeyeceği üzerine bir özür diledi.&nbsp;Ancak oyun o yıl Nisan\'da çıkışını yaptığında heykelin hâlâ dahil olması daha fazla tepki çekti.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ocak 2019\'da Deep Silver,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Epic Games</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile yeni çıkacak oyunları&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Metro Exodus</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'un PC versiyonunun Epic\'in dijital dağıtım mağazasına özel olması için bir yıllık bir anlaşma imzaladı.&nbsp;Bu anlaşma nedeniyle Deep Silver\'ın Ağustos 2018\'den beri ön sipariş aldığı başka bir dijital dağıtım mağazası olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Steam</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'den Metro Exodus kaldırıldı.&nbsp;Buna ek olarak anlaşmanın oyunun çıkışına üç hafta kala yapılması bazı kişilerce eleştirildi. Steam\'in arkasındaki şirket olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Valve</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;bu hareketi tüketicilere haksızlık olarak nitelendirdi.</span></p>', 1, 1, '/uploads/publishers/deep-silver/deep-silver.jpg', NULL, '2022-01-03 16:35:04', '2022-01-04 13:09:17'),
(5, 'Microsoft', 'microsoft', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Microsoft Corporation,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ABD</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çok uluslu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;teknoloji şirketidir. Bilgisayar yazılımları, elektronik cihazlar, kişisel bilgisayarlar ve bilişim hizmetleri geliştirir, üretir, lisanslar ve satar. Şirketin en çok bilinen yazılımları&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">MS-DOS</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;işletim sistemi aileleri,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Office</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;paketi,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bing</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;adlı arama motoru,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Internet Explorer</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Edge</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;web tarayıcılarıdır. Öne çıkan donanım ürünleri ise&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;video oyun konsolları,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Hololens</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;sanal gerçeklik gözlükleri ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Surface</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;dokunmatik ekranlı kişisel bilgisayar ürün ailesidir. Microsoft, 2016 itibarıyla dünyanın en büyük yazılım geliştiricisi ve dünyanın en değerli şirketlerinden birisidir. Şirketin adı, \"microcomputer\" (mikrobilgisayar) ve \"software\" (yazılım) sözcüklerinin birleşmesinden meydana gelir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Microsoft, 4 Nisan 1975\'te&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Paul Allen</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bill Gates</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından kuruldu. İkilinin amacı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Altair 8800</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;bilgisayar sistemi için&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">BASIC</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yorumlayıcıları</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;geliştirip satmaktı. Şirket, 1980\'lerin ortalarında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">MS-DOS</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile kişisel bilgisayarlara yönelik işletim sistemi pazarını domine etmeye başladı ve ardından&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ile liderliğini sürdürdü.1986\'da halka açıldı. Hisse değerleri hızla yükselince üç dolar milyarderi yarattı, Microsoft çalışanlarından 12.000 kişi de dolar milyoneri oldu. Şirket, 1990\'lardan bu yana işletim sistemi pazarının dışına çıkarak genişlemekte, ayrıca büyük çaplı kurumsal alımlar yapmaktadır. En büyük satın alımları Aralık 2016\'da 26,2 milyar dolara aldığı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">LinkedIn</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve Mayıs 2011\'de 8,5 milyar dolara aldığı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Skype Technologies</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'dir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">2015 itibarıyla Microsoft,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PC</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;işletim sistemleri pazarında ve ofis yazılımları pazarında pazar lideridir, ancak tüm işletim sistemleri hesaba katıldığında liderliği&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Android</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'e kaptırmıştır. Şirket ayrıca masaüstü bilgisayarlar ve sunucular için hem bireylere hem de kurumlara yönelik çeşitli yazılım ve hizmetler geliştirmektedir. Bunlar arasında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bing</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;arama motoru,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">MSN</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;adlı dijital hizmetler pazarı,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">HoloLens</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;adlı karma gerçeklik platformu,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Azure</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;bulut bilişim platformu ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Visual Studio</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yazılım geliştirme platformu sayılabilir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">2000 yılında Gates&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">CEO</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'luk görevinden ayrıldı ve yerine&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Steve Ballmer</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;geldi. Ballmer, Microsoft\'u bir \"cihaz ve hizmet şirketine dönüştürme\" stratejisini benimsedi. Bu kapsamda Microsoft, 2008\'de Danger Inc.\'i satın alarak kendi bilgisayarlarını üretebilir hale geldi ve Haziran 2012\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Surface</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tablet bilgisayar serisini duyurdu. Ardından\'dan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Nokia</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nın cihaz ve hizmetler bölümünü satın alarak&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft Mobile</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ı oluşturdu. 2014\'te CEO\'luk görevine&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Satya Nadella</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;geldikten sonra şirket, donanım bölümünü küçültmeye ve bulut bilişime odaklanmaya başladı. Bu hamleyle birlikte şirketin hisse değeri Aralık 1999\'dan beri en yüksek noktaya ulaştı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">2020 yılında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bill Gates</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;Teknoloji Danışmanlığı görevinden istifa etti.&nbsp;Microsoft&nbsp;ise onun yerine İcra Kurulu Başkanı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Satya Nadella</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'yı teknoloji danışmanı pozisyonuna getirdi.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Tarihçe\" style=\"font-size: 18px; font-family: Helvetica;\"><b>Tarihçe</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Şirket 1975 yılında Amerika Birleşik Devletleri\'nin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Washington</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;eyaletindeki&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Seattle</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kentinde iki üniversite öğrencisi tarafından kurulmuştur. Bu iki girişimcinin vizyonu \"her ev ve her masada bir bilgisayar\"dı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Popular Electronics</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;dergisinin 1 Ocak 1975 tarihli sayısında&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Altair 8800</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;bilgisayar sisteminin tanıtımını okuduktan birkaç gün sonra&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bill Gates</span><span style=\"font-size: 14px; font-family: Helvetica;\">, bilgisayarın tasarımcısı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">MITS</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;(Micro Instrumentation and Telemetry Systems) ile temasa geçti. Ekibi ile birlikte Altair 8800 üzerinde çalışan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">BASIC</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yazılımlama dili geliştirdiklerini belirtti.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Paul Allen</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">MITS</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'e yazılımın tanıtımını yapmaya gitti.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Paul Allen</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;Altair 8800\'i daha önce kullanmamış olmasına karşın tanıtımı başarılı oldu. Tanıtımın sonunda MITS(Micro Instrumentation and Telemetry Systems) Bill Gates ve Paul Allen\'dan Altair bilgisayarları için BASIC yazılımlama dilinin telif hakkını satın aldı. Kârlı bir iş fırsatı yakaladıkları düşüncesiyle Bill Gates&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Harvard Üniversitesi</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ndeki hukuk eğitimini yarıda bırakıp&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">New Mexico</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;eyaletinin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Albuquerque</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şehrinde Microsoft şirketini kurdu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Şirket, ilk uluslararası bürosunu 1 Kasım 1978\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Japonya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da açtı. 1 Ocak 1979\'da şirket merkezini Washington eyaletinin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bellevue</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şehrine taşıdı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Aradan geçen yıllar içinde Microsoft bir dünya devi olmuş, sahibi Bill Gates\'i dünyanın en zengin insanı haline getirmiştir. Ancak bu zenginliği herkesin düşündüğü şekilde sadece geliştirdiği işletim sistemini satarak değil ayrıca sektördeki büyük açıkları kapatabilecek nitelikte yazılan yazılımların daha kendisi için tehlike teşkil edecek aşamaya getirmeden satın almasıyla başarabilmiştir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Microsoft , başarılı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İsveçli</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;yapımcı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Mojang</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketini de 2.5 milyar $ fiyatına satın almıştır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Microsoft artık sadece bir yazılım şirketi değil ayrıca iş ve eğlence dünyası için geliştirdiği donanımlar ile de adından söz ettirmektedir.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Visual Studio</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">FSX</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;(Microsoft Flight Simulator) gibi büyük ve önemli çalışmaları da bulunmaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Microsoft 17 Eylül 2007 tarihinde&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avrupa Birliği Microsoft rekabet davasını</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kaybetmiştir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">3 Eylül 2013 tarihinde Microsoft,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Nokia</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nın Cihazlar ve Servisler birimlerini ve bazı Nokia patentlerini 7.2 Milyar Dolar karşılığında satın alınmıştır.&nbsp;Bu satın almayla birlikte Nokia\'nın 18 bin\'i üretimde olmak üzere, 32 bin çalışanının da Microsoft tarafına geçeceği açıklandı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">18 Mayıs 2016\'da ise Microsoft akıllı telefonlardan beklediği verimi alamadığını belirterek Nokia\'yı 350 Milyon Dolara&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Foxconn</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'a sattığını açıkladı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">14 Haziran 2016 tarihinde Microsoft sosyal medya platformu olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">LinkedIn</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'i 26.2 Milyar Dolara satın almıştır. LinkedIn Microsoft\'un satın aldığı 196. şirket olmuştur.</span></p>', 1, 1, '/uploads/publishers/microsoft/microsoft.jpg', NULL, '2022-01-10 12:00:10', '2022-01-13 09:02:48');
INSERT INTO `publishers` (`id`, `name`, `slug`, `description`, `games_count`, `status`, `image`, `deleted_at`, `created_at`, `updated_at`) VALUES
(6, 'Sony Interactive Entertainment', 'sony-interactive-entertainment', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sony Interactive Entertainment (SIE),&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sony</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve dijital eğlence alanında faaliyet gösteren&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yan kuruluşudur</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Şirket, 16 Kasım 1993\'te Sony Computer Entertainment adıyla&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyun konsollarını üretmek amacıyla kuruldu.&nbsp;Şirketin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzey Amerika</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve global merkezi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">San Mateo</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da yer almakta olup&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Asya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketi olan Sony Interactive Entertainment Japan Asia&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tokyo</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avrupa</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketi olan Sony Interactive Entertainment Europe&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Londra</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da yer almaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sony Interactive Entertainment,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;video oyun sistemleri için hem donanım hem de yazılımın araştırılması, geliştirilmesi, üretimi ve satışı ile ilgilenmektedir. SIE aynı zamanda video oyunlarının geliştiricisi ve yayıncısı olup Sony\'nin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avrupa</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzey Amerika</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Asya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Okyanusya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;olmak üzere en büyük pazarlarında çeşitli iştiraklere sahiptir. Ağustos 2018\'e kadar, şirket dünya çapında 525 milyondan fazla PlayStation konsolu sattı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sony Interactive Entertainment, Japonya\'nın en büyük 292. şirketi olup en büyük dördüncü oyun şirketidir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Tarihçe\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Tarihçe</b></span></h2><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\"Kurulu.C5.9Fu_ve_ilk_y.C4.B1llar.C4.B1\" style=\"\"></span><span class=\"mw-headline\" id=\"Kuruluşu_ve_ilk_yılları\" style=\"font-size: 18px; font-family: Helvetica;\">Kuruluşu ve ilk yılları</span></b></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sony Interactive Entertainment, Sony Computer Entertainment International adıyla 16 Kasım 1993\'te, Japonya\'da kuruldu.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sony Music</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;çatısı altında kurulan şirketin temelleri,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Ken Kutaragi</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Akira Sato</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve Sony Music CEO\'su&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Shigeo Maruyama</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından atıldı. Şirket, kuruluşunun ardından Sony Music ile ortak çalışmaya, Sony Music\'in CD-ROM\'lar hakkındaki bilgi ve deneyimlerinden yararlanmaya başladı. Resmî olarak Terry Tokunaka tarafından kurulan şirketin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;projesinin başında ise Ken Kutaragi yer almaktaydı. Sonraları şirketin başına geçen Ken Kutaragi, \"PlayStation\'ın Babası\" olarak da bilinmektedir.</span></p><h3 style=\"margin: 0.3em 0px 0px; padding-top: 0.5em; padding-bottom: 0px; overflow: hidden; line-height: 1.6;\"><b><span id=\"Geli.C5.9Fimi\" style=\"\"></span><span class=\"mw-headline\" id=\"Gelişimi\" style=\"font-size: 18px; font-family: Helvetica;\">Gelişimi</span></b></h3><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">1 Temmuz 2002\'de şirketin başına&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Shigeo Maruyama</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nın yerine&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tamotsu Iba</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;geçti.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Jack Tretton</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Phil Harrison</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ise ikinci başkanlık statüsüne getirildi.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">14 Eylül 2005\'te SCE, kendisine bağlı olarak çalışan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sony Computer Entertainment Worldwide Studios</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'u (SCE WWS) kurdu.&nbsp;SCE, bu şirketin kurulmasıyla yazılım üretimi ve geliştirilmesini amaçlıyordu.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Shuhei Yoshida</span><span style=\"font-size: 14px; font-family: Helvetica;\">, 16 Mayıs 2008\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kazuo Hirai</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin yerine SCE WWS\'nin başına geçti.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">2016 yılında Sony Computer Entertainment, adını Sony Interactive Entertainment olarak değiştirdi.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><b><span id=\"Yaz.C4.B1l.C4.B1m_geli.C5.9Ftirme_st.C3.BCdyolar.C4.B1\" style=\"\"></span><span class=\"mw-headline\" id=\"Yazılım_geliştirme_stüdyoları\" style=\"font-size: 24px; font-family: Helvetica;\">Yazılım geliştirme stüdyoları</span></b></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sony Interactive Entertainment\'e bağlı olarak çalışan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">SIE Worldwide Studios</span><span style=\"font-size: 14px; font-family: Helvetica;\">, dünya genelinde Sony Interactive Entertainment etiketiyle video oyunu geliştiriciliği ve dağıtımcılığı yapmaktadır.</span></p>', 2, 1, '/uploads/publishers/sony-interactive-entertainment/sony-interactive-entertainment.png', NULL, '2022-01-12 16:56:04', '2022-02-23 13:28:50'),
(7, 'Xbox Game Studios', 'xbox-game-studios', '<p><span>﻿</span><span style=\"font-size: 14px; font-family: Helvetica;\">Xbox Game Studios,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox 360</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox One</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Windows</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Steam</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Windows Store</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Windows Phone</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;platformları için oyun üretimi yapan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Microsoft</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'un&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;prodüksiyon kanadıdır. 2002 yılında&nbsp;Microsoft Game Studios&nbsp;adıyla kurulmuş, 2011 yılında yeniden markalaşma yoluna gitmiş ve şimdiki adını almıştır. Bu departman 2002 yılından öncesinde Microsoft Game Division veya kısa haliyle Microsoft Games olarak adlandırılırdı. Microsoft Studios kendi etiketi altında birinci veya üçüncü parti geliştirme stüdyolarıyla beraber oyunlar geliştirmekte ve yayınlamaktadır.&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">MSN Oyunlar</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;da Studios\'un bir parçası olmuştur.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Tarihçe\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Tarihçe</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">2007 yılında, Microsoft Studios&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Reading</span><span style=\"font-size: 14px; font-family: Helvetica;\">, İngiltere\'de bir ofis açarak Avrupa ofislerini duyurmuş oldu. Bu ofisin genel müdürlüğüne&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Phil Spencer</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;getirildi.&nbsp;Ardından&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">MechWArrior</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisi ile tanınan&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">FASA Studio</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketini bünyesine kattı.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Halo</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun eski geliştiricisi olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bungie</span><span style=\"font-size: 14px; font-family: Helvetica;\">, Microsoft Studios bünyesinden ayrılarak bağımsız özel bir şirket olmayı istediğini duyurdu. 17 Temmuz 2007\'de, Peter Moore Microsoft Studios\'dan ayrılıp Electronic Arts\'ın spor oyunları biriminin başına geçti. Temmuz 2007\'de Don Mattrick Kıdemli Başkan Yardımcısı olarak Microsoft Studios\'ta görevine başladı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">2008 yılında, Microsoft Studios kart oyunları geliştiren&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Carbonated Games</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kapattı ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox Live Productions</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Xbox Live Arcade</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;için \"yüksek-kalite dijital içerik\" üreteceğini duyurdu. Peşinden&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Bungie</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin bağımsız bir statüde olmasından dolayı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">343 Industries</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'e&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Halo</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;serisinin geliştirilmesini devretti. 2009 yılının ortalarında Halo serisine dair ilk yayınlamalarını yapan 343 Industries,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Halo Legends</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;isminde bir animasyon projesi oluşturdu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">2009 yılında,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Ensemble Studios</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Aces Studio</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketlerini ekonomik krizden dolayı kapattı. Ekibin içerisinde yer alanları bünyesine katarak kendi oyun stüdyosunu yeniden yapılandırmaya başladı. Aynı yıl Başkan yardımcısı Shane Kim\'in emekliliğininden sonra Phil Spencer, Microsoft Studios\'un Başkan yardımcılığına getirildi.</span></p><p><br></p>', 1, 1, '/uploads/publishers/xbox-game-studios/xbox-game-studios.png', NULL, '2022-01-13 07:57:17', '2022-01-13 12:02:21'),
(8, 'Krafton Game Union', 'krafton-game-union', '<p><span style=\"font-size: 14px; font-family: Helvetica;\">Krafton Game Union&nbsp;,&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Güney Kore</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketidir. Şirket, Bluehole adıyla Mart 2007 tarihinde Chang Byung-gyu tarafından kurulmuştur.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">TERA</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayerUnknown\'s Battlegrounds</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PUBG: NEW STATE</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyunları ile bilinmektedir. 5 Kasım 2018\'de birleşik bir marka altında Krafton Game Union adıyla yeniden kurulmuştur.</span><br></p>', 1, 1, '/uploads/publishers/krafton-game-union/krafton-game-union.png', NULL, '2022-01-13 12:14:18', '2022-01-13 13:12:38'),
(9, 'Ubisoft', 'ubisoft', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ubisoft,&nbsp;Ubisoft Entertainment&nbsp;(ya da eski adıyla&nbsp;Ubi Soft)&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Fransız</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyun</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;geliştiricisi ve yayımcısıdır. Kısmen(%20) Amerikalı Electronic Arts ile ortaktır, fakat yine de Electronic Arts firmasından bağımsız olarak çalışmaktadır. Ubisoft firması, 28 ülkede stüdyoları ile dünya çapında bir varlığa sahiptir.</span><br><span style=\"font-size: 14px; font-family: Helvetica;\">Aynı zamanda 2008 yılı itibarıyla Ubisoft firması Avrupa\'nın 3.büyük video oyun ve yapımcı firması seçilmiştir. Ubisoft 2002-2003 yılları arasında 453 milyon, 2003-2004 yılları arasında 508 milyon&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">€</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kâr elde etmiştir. Ve şirkette birçok çalışan vardır. Assasins Creed, Far Cry gibi oyunlarla tanınmaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tom Clancy’s The Division</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve Far Cry Primal oyunları 2016 yılının ilk çeyreğinde&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica;\">Amerika Birleşik Devletleri</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nde en çok satılan iki oyun olmuştur. Bu satışlarıyla 2016 yılının başında en başarılı oyun şirketi unvanını kazanmıştır.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Tarihi\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Tarihi</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Guillemot ailesinin 5 kardeşi bilgisayar oyunu yayımcısı olarak 1986 yılında Fransa\'da Ubisoft\'u kurdu. İlerleyen süreçte Ubisoft Electronic Arts, Sierra On-Line gibi firmalarla anlaşma yaparak gelişmeye başladı ve Amerika Birleşik Devletleri, İngiltere gibi ülkelerin pazarlarına da girmeye başladı.</span><br><span style=\"font-size: 14px; font-family: Helvetica;\">1990 yılında Ubisoft Montreal adlı bir stüdyo açtı ve burası Ubisoft\'un genel merkezi oldu ve şirket daha sonra burada oyun geliştirme programlarına girişti. Ubisoft 1996 yılında birçok yerde ofis açarak dünya çapında genişlemeye başladı.</span></p><ul style=\"list-style-image: url(&quot;/w/skins/Vector/resources/common/images/bullet-icon.svg?d4515&quot;); margin: 0.3em 0px 0px 1.6em; padding: 0px;\"><li style=\"margin-bottom: 0.1em;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Şubat 2001\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Almanya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Blue Byte Software</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketini satın aldı.</span></li></ul><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Merkezi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Montreuil</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Fransa</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da yer almaktadır. 20 ülkede stüdyosu bulunmakla birlikte,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Montreal</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-family: Helvetica; font-size: 14px;\">K</span><span style=\"font-family: Helvetica; font-size: 14px;\">anada</span></span><span style=\"font-size: 14px; font-family: Helvetica;\">;&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Barselona</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">İspanya</span><span style=\"font-size: 14px; font-family: Helvetica;\">;&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Şangay</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Çin</span><span style=\"font-size: 14px; font-family: Helvetica;\">;&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Kuzey Karolina</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">ABD</span><span style=\"font-size: 14px; font-family: Helvetica;\">;&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Düsseldorf</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Almanya</span><span style=\"font-size: 14px; font-family: Helvetica;\">; ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">Milano</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">İtalya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da geliştirme stüdyoları bulunmaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Gelecekteki Oyunlar sayfası yapım aşamasında olup en kısa sürede hizmete açılacaktır.</span></p>', 1, 1, '/uploads/publishers/ubisoft/ubisoft.jpg', NULL, '2022-01-13 14:26:56', '2022-01-14 09:27:25');
INSERT INTO `publishers` (`id`, `name`, `slug`, `description`, `games_count`, `status`, `image`, `deleted_at`, `created_at`, `updated_at`) VALUES
(10, 'Sega', 'sega', '<p style=\"margin: 0.5em 0px;\"><span>﻿</span><span style=\"font-size: 14px; font-family: Helvetica;\">Sega Corporation,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Japonya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çok uluslu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">geliştirme</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">yayımlama</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica; font-size: 14px;\">şirketidir</span><span style=\"font-size: 14px; font-family: Helvetica;\">.</span><span style=\"font-size: 14px; font-family: Helvetica;\"> Şirket, 3 Haziran 1960 tarihinde kurulmuş olup&nbsp;ağırlıklı olarak video oyunları ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">arcade oyun makineleri</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;üretmektedir. Şirketin merkezi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tokyo</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Shinagawa</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;semtinde yer almakta olup&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avrupa</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketi olan Sega Europe&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Londra</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kuzey Amerika</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;şirketi olan Sega of America&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Irvine</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da yer almaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sega,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sonic the Hedgehog</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Total War</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Yakuza</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;dahil olmak üzere milyonlarca satan birçok oyun serisine sahiptir ve dünyanın en üretken arcade oyun üreticisidir. Aynı zamanda eğlence parkları da işletmekte ve Sega Toys dahil diğer eğlence ürünlerini üretmektedir. Bir şirket olarak Sega, zamanında kendi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyun konsollarını</span><span style=\"font-size: 14px; font-family: Helvetica;\">, yaratıcılığını ve yeniliklerini destekleyen zamanı nedeniyle geriye dönük olarak hatırlanır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sega, 2013 verilerine göre Japonya\'nın en büyük 363. şirketi ve en büyük altıncı oyun şirketidir.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Tarihçe\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Tarihçe</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sega\'nın tarihi 1940 yılında Amerikalı işadamları Martin Bromley, Irving Bromberg ve James Humpert tarafından kurulan Standard Games\'e kadar gitmektedir.&nbsp;Service Games\'in kısaltması olan Sega adı&nbsp;ilk kez 1954 yılında Diamond Star adlı jetonlu makinede kullanıldı.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Günümüz Sega şirketi ise 3 Haziran 1960 tarihinde Martin Bromley ve Richard Stewart tarafından Nihon Goraku Bussan adıyla kuruldu.&nbsp;Şirket,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">jetonlu oyun</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ithalatçısı Rosen Enterprises\'ı satın aldıktan sonra Sega Enterprises, Ltd. olarak tanındı. Sega,&nbsp;Periscope&nbsp;adlı ilk jetonlu oyununu 1960\'ların sonunda geliştirdi.&nbsp;1969\'da Sega, Gulf ve Western Industries\'e satıldı. 1973\'te şirket&nbsp;Pong-Tron&nbsp;adlı ilk&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyununu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;geliştirdi.&nbsp;Sega, 1980\'li yıllar boyunca&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hang-On</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Out Run</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Daytona USA</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi birçok başarılı arcade oyunu piyasaya sürdü.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">1980\'lerin başlarında arcade pazarında yaşanan düşüşün ardından Sega,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Nintendo Entertainment System</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi rakiplere karşı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">SG-1000</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve Mark III\'yi (</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Master System</span><span style=\"font-size: 14px; font-family: Helvetica;\">)&nbsp;geliştirerek&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu konsolu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;pazarına girdi.&nbsp;1984 yılında Sega yöneticileri David Rosen ve Hayao Nakayama, CSK Corporation\'dan destek alarak şirketin satın alınmasına önderlik etti.&nbsp;Sega, 1988\'de bir sonraki konsolu olan&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sega Mega Drive</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ı piyasaya sürdü.&nbsp;Japonya\'da pek ilgi görmemesine rağmen Mega Drive, 1991\'de&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sonic the Hedgehog</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;oyununun piyasaya sürülmesinden sonra büyük bir başarı yakaladı ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ABD</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'de (\"Sega Genesis\" adyıla) ana rakibi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Super Nintendo Entertainment System</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'den daha çok sattı.&nbsp;Ayrıca&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sonic</span><span style=\"font-size: 14px; font-family: Helvetica;\">, şirketin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Mario</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;karşısında başarısız olan önceki maskotu&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Alex Kidd</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'in yerine&nbsp;yeni maskotu oldu.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Ancak Sega, sonraki on yıl boyunca&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">32X</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Saturn</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Dreamcast</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;konsolları ile ticari başarısızlıklar yaşadı.&nbsp;Özellikle&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sony</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin yeni piyasaya sürülen&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">PlayStation 2</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;konsoluna karşı pazarda büyük kayıplar yaşayan Sega, 2001\'de video oyunu konsolu pazarından çekildi&nbsp;ve 2004\'te&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sammy Corporation</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;tarafından satın alınarak&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sega Sammy Holdings</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;adlı ortak şirket kuruldu.&nbsp;Bu satın alımdan bu yana geçen yıllarda, Sega daha kârlı hale geldi.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Nisan 2015 tarihinde Sega Holdings Co., Ltd. kuruldu ve Sega Corporation, Sega Games Co. Ltd. olarak adlandırılarak arcade, eğlence ve oyuncak bölümleri diğer şirketlere ayrıldı.&nbsp;1 Nisan 2020 tarihinde Sega Games ve Sega Interactive birleşerek tekrar Sega Corporation adını aldı. Sega Holdings ise Sega Group Corporation adını aldı.&nbsp;Eylül 2020 tarihinde şirket Sega Entertainment\'in büyük bir kısmını Genda Inc\'e satarak arcade oyun pazarından çekilme kararı aldı.</span></p><h2 style=\"margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); line-height: 1.3;\"><span class=\"mw-headline\" id=\"Kurumsal_yapı\" style=\"font-size: 24px; font-family: Helvetica;\"><b>Kurumsal yapı</b></span></h2><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sega\'nın genel merkezi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Japonya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nın başkenti&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tokyo</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Shinagawa</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;semtinde bulunan Sumitomo Fudosan Osaki Garden Tower\'da yer almaktadır. Ayrıca, Sega\'nın&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">ABD</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Kaliforniya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;eyaletine bağlı&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Irvine</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;kentinde Sega of America,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Birleşik Krallık</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'ın başkenti&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Londra</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da Sega Europe&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Güney Kore</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nin başkenti&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Seul</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'de Sega Publishing Korea&nbsp;ile&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Singapur</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hong Kong</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Şanghay</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Taipei</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'de ofisleri bulunmaktadır.&nbsp;Diğer bölgelerde Sega,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Brezilya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'daki Tectoy gibi video oyun ve konsollar için distribütörlük anlaşması yaptı.&nbsp;Sega\'nın&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Fransa</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Almanya</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">İspanya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Avustralya</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'da da ofisleri vardı, bu pazarlar o zamandan beri distribütörlük yaptılar.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sega, günümüzde&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Amplitude Studios</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Atlus</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Creative Assembly</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Demiurge Studios</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Hardlight</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Relic Entertainment</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sports Interactive</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Two Point Studios</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi üçüncü taraf stüdyolara da sahiptir.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Sega\'nın&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">oyun salonları</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;Club Sega&nbsp;adıyla bilinmekte olup&nbsp;şirketin&nbsp;Joypolis&nbsp;ve&nbsp;SegaWorld&nbsp;adlı tema parkları da bulunmaktadır.</span></p>', 1, 1, '/uploads/publishers/sega/sega.png', NULL, '2022-02-16 09:37:30', '2022-02-16 11:25:56'),
(11, 'Bandai Namco Entertainment', 'bandai-namco-entertainment', '<p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bandai Namco Entertainment, Inc.&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Japonya</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;merkezli bir&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">çok uluslu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">video oyunu</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">geliştirme</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">yayımlama</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">şirketidir</span><span style=\"font-size: 14px; font-family: Helvetica;\">. Şirket, 31 Mart 2006 tarihinde Namco Bandai Games adıyla kurulmuş olup&nbsp;1 Nisan 2015 tarihinde Bandai Namco Entertainment adını aldı. Şirketin merkezi&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tokyo</span><span style=\"font-size: 14px; font-family: Helvetica;\">\'nun&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Minato</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;semtinde yer almaktadır.</span></p><p style=\"margin: 0.5em 0px;\"><span style=\"font-size: 14px; font-family: Helvetica;\">Bandai Namco Entertainment,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tekken</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Pac-Man</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Galaxian</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Gundam</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Katamari</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Tales of</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Soulcalibur</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Ace Combat</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Taiko no Tatsujin</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;de dahil olmak üzere milyondan fazla satış yapan çeşitli video oyunu serisine sahiptir ve Japonya\'nın üçüncü ve dünyanın yedinci büyük video oyunu şirketidir. Şirket, ayrıca&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Digimon</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Dragon Ball</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">One Piece</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Naruto</span><span style=\"font-size: 14px; font-family: Helvetica;\">,&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">Sailor Moon</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;ve&nbsp;</span><span style=\"background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 14px; font-family: Helvetica;\">My Hero Academia</span><span style=\"font-size: 14px; font-family: Helvetica;\">&nbsp;gibi serilerin lisanslarına da sahiptir. Son olarak From Software tarafından geliştirilen Elden Ring adlı oyunun yayımcılığını yapmaktadır.</span></p>', 0, 1, '/uploads/publishers/bandai-namco-entertainment/bandai-namco-entertainment.png', NULL, '2022-03-03 12:26:37', '2022-03-03 12:26:38');

